import React, { useState, useEffect } from 'react';
import { Search, X, FileText, Users, FolderOpen, Clock, CheckCircle } from 'lucide-react';
import Card from '../UI/Card';

const GlobalSearch = ({ isOpen, onClose, onSearch }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // Mock search results - replace with actual API call
  const mockSearchResults = [
    {
      id: 1,
      type: 'task',
      title: 'Implement user authentication',
      description: 'Add login and registration functionality',
      project: 'KSSEM CSBS',
      status: 'In Progress',
      icon: CheckCircle,
      color: 'text-blue-600'
    },
    {
      id: 2,
      type: 'project',
      title: 'KSSEM CSBS',
      description: 'Teachers attendance and leave management System',
      status: 'Active',
      icon: FolderOpen,
      color: 'text-green-600'
    },
    {
      id: 3,
      type: 'user',
      title: 'John Doe',
      description: 'Team Member',
      project: 'KSSEM CSBS',
      status: 'Active',
      icon: Users,
      color: 'text-purple-600'
    },
    {
      id: 4,
      type: 'task',
      title: 'Database design',
      description: 'Create database schema for attendance system',
      project: 'KSSEM CSBS',
      status: 'Completed',
      icon: CheckCircle,
      color: 'text-green-600'
    }
  ];

  useEffect(() => {
    if (searchQuery.length > 2) {
      setIsLoading(true);
      // Simulate API delay
      setTimeout(() => {
        const filtered = mockSearchResults.filter(item =>
          item.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
          item.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
          item.project.toLowerCase().includes(searchQuery.toLowerCase())
        );
        setSearchResults(filtered);
        setIsLoading(false);
      }, 300);
    } else {
      setSearchResults([]);
    }
  }, [searchQuery]);

  const handleResultClick = (result) => {
    onSearch(result);
    setSearchQuery('');
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-start justify-center pt-20">
      <Card className="w-full max-w-2xl mx-4 bg-white shadow-2xl">
        <div className="p-4 border-b border-gray-200">
          <div className="flex items-center gap-3">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="text"
                placeholder="Search tasks, projects, users..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                autoFocus
              />
            </div>
            <button
              onClick={onClose}
              className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <X className="h-5 w-5 text-gray-500" />
            </button>
          </div>
        </div>

        <div className="max-h-96 overflow-y-auto">
          {isLoading ? (
            <div className="p-8 text-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
              <p className="mt-2 text-gray-500">Searching...</p>
            </div>
          ) : searchResults.length > 0 ? (
            <div className="p-2">
              {searchResults.map((result) => {
                const Icon = result.icon;
                return (
                  <div
                    key={result.id}
                    onClick={() => handleResultClick(result)}
                    className="flex items-center gap-3 p-3 hover:bg-gray-50 rounded-lg cursor-pointer transition-colors"
                  >
                    <div className={`p-2 rounded-lg bg-gray-100 ${result.color}`}>
                      <Icon className="h-4 w-4" />
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2">
                        <h4 className="font-medium text-gray-900 truncate">{result.title}</h4>
                        <span className={`px-2 py-1 text-xs rounded-full ${
                          result.status === 'Completed' ? 'bg-green-100 text-green-800' :
                          result.status === 'In Progress' ? 'bg-yellow-100 text-yellow-800' :
                          result.status === 'Active' ? 'bg-blue-100 text-blue-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {result.status}
                        </span>
                      </div>
                      <p className="text-sm text-gray-500 truncate">{result.description}</p>
                      {result.project && (
                        <p className="text-xs text-gray-400">Project: {result.project}</p>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          ) : searchQuery.length > 2 ? (
            <div className="p-8 text-center text-gray-500">
              <Search className="h-12 w-12 mx-auto mb-4 text-gray-300" />
              <p>No results found for "{searchQuery}"</p>
            </div>
          ) : (
            <div className="p-8 text-center text-gray-500">
              <Search className="h-12 w-12 mx-auto mb-4 text-gray-300" />
              <p>Start typing to search...</p>
            </div>
          )}
        </div>
      </Card>
    </div>
  );
};

export default GlobalSearch;
