import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';
import { Bar, Doughnut, Line } from 'react-chartjs-2';
import Card from '../UI/Card';
import { TrendingUp, Users, Clock, CheckCircle, AlertCircle, XCircle } from 'lucide-react';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

const ProjectAnalytics = ({ analytics, projectName, isLoading = false }) => {
  // Helper function to format duration
  const formatDuration = (days, minutes) => {
    if (!days && !minutes) return 'N/A';
    
    const daysInt = Math.floor(days);
    const hours = Math.floor((days - daysInt) * 24);
    const mins = Math.floor(minutes % 60);
    
    if (daysInt > 0) {
      return `${daysInt}d ${hours}h ${mins}m`;
    } else if (hours > 0) {
      return `${hours}h ${mins}m`;
    } else {
      return `${mins}m`;
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {[1, 2, 3, 4].map((i) => (
            <Card key={i} className="p-6 animate-pulse">
              <div className="h-4 bg-gray-200 rounded mb-2"></div>
              <div className="h-8 bg-gray-200 rounded"></div>
            </Card>
          ))}
        </div>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {[1, 2].map((i) => (
            <Card key={i} className="p-6 animate-pulse">
              <div className="h-6 bg-gray-200 rounded mb-4"></div>
              <div className="h-64 bg-gray-200 rounded"></div>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (!analytics) {
    return (
      <div className="text-center py-12">
        <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
          <TrendingUp className="h-8 w-8 text-gray-400" />
        </div>
        <p className="text-lg text-gray-500">No analytics data available</p>
        <p className="text-sm text-gray-400 mt-2">Analytics will appear here once tasks are created</p>
      </div>
    );
  }

  // Simplified analytics for general use (Dashboard, etc.)
  if (!detailed) {
    return (
      <div className="space-y-4">
        {/* Simple Stats Grid */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <Card className="p-4 text-center">
            <p className="text-sm text-gray-600">Total Tasks</p>
            <p className="text-2xl font-bold text-gray-900">{analytics.total_tasks || 0}</p>
          </Card>
          <Card className="p-4 text-center">
            <p className="text-sm text-gray-600">Completed</p>
            <p className="text-2xl font-bold text-green-600">{analytics.completed_tasks || 0}</p>
          </Card>
          <Card className="p-4 text-center">
            <p className="text-sm text-gray-600">In Progress</p>
            <p className="text-2xl font-bold text-blue-600">{analytics.in_progress_tasks || 0}</p>
          </Card>
          <Card className="p-4 text-center">
            <p className="text-sm text-gray-600">Completion Rate</p>
            <p className="text-2xl font-bold text-purple-600">
              {analytics.completion_rate !== undefined 
                ? `${analytics.completion_rate}%`
                : (analytics.total_tasks > 0 
                    ? Math.round((analytics.completed_tasks / analytics.total_tasks) * 100)
                    : 0) + '%'}
            </p>
          </Card>
        </div>
      </div>
    );
  }

  // Prepare data for charts
  const taskStatusData = {
    labels: ['Completed', 'In Progress', 'To Do'],
    datasets: [
      {
        data: [
          analytics.completed_tasks || 0,
          analytics.in_progress_tasks || 0,
          analytics.todo_tasks || 0,
        ],
        backgroundColor: [
          '#10b981', // Green for completed
          '#3b82f6', // Blue for in progress
          '#f59e0b', // Yellow for todo
        ],
        borderColor: [
          '#059669',
          '#2563eb',
          '#d97706',
        ],
        borderWidth: 2,
        hoverOffset: 4,
      },
    ],
  };

  // Process priority data from backend
  const priorityData = {
    labels: ['High', 'Medium', 'Low'],
    datasets: [
      {
        data: [
          analytics.tasks_by_priority?.find(p => p.priority === 'high')?.count || 0,
          analytics.tasks_by_priority?.find(p => p.priority === 'medium')?.count || 0,
          analytics.tasks_by_priority?.find(p => p.priority === 'low')?.count || 0,
        ],
        backgroundColor: [
          '#ef4444', // Red for high
          '#f59e0b', // Yellow for medium
          '#10b981', // Green for low
        ],
        borderColor: [
          '#dc2626',
          '#d97706',
          '#059669',
        ],
        borderWidth: 2,
        hoverOffset: 4,
      },
    ],
  };

  // Show completion rate and task distribution
  const progressData = {
    labels: ['Completed', 'In Progress', 'To Do'],
    datasets: [
      {
        label: 'Tasks',
        data: [
          analytics.completed_tasks || 0,
          analytics.in_progress_tasks || 0,
          analytics.todo_tasks || 0,
        ],
        backgroundColor: [
          '#10b981', // Green for completed
          '#3b82f6', // Blue for in progress
          '#f59e0b', // Yellow for todo
        ],
        borderColor: [
          '#059669',
          '#2563eb',
          '#d97706',
        ],
        borderWidth: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
        labels: {
          padding: 20,
          usePointStyle: true,
        },
      },
      tooltip: {
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        titleColor: 'white',
        bodyColor: 'white',
        borderColor: 'rgba(255, 255, 255, 0.1)',
        borderWidth: 1,
      },
    },
  };

  const doughnutOptions = {
    ...chartOptions,
    cutout: '60%',
    plugins: {
      ...chartOptions.plugins,
      legend: {
        position: 'right',
        labels: {
          padding: 20,
          usePointStyle: true,
        },
      },
    },
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="text-center mb-8">
        <h3 className="text-2xl font-bold text-gray-900 mb-2">
          {projectName ? `${projectName} Analytics` : 'Project Analytics'}
        </h3>
        <p className="text-gray-600">Comprehensive insights and performance metrics</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
        <Card className="p-6 hover-lift">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Tasks</p>
              <p className="text-3xl font-bold text-gray-900">{analytics.total_tasks || 0}</p>
            </div>
            <div className="p-3 bg-blue-100 rounded-xl">
              <CheckCircle className="h-6 w-6 text-blue-600" />
            </div>
          </div>
        </Card>

        <Card className="p-6 hover-lift">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Completed</p>
              <p className="text-3xl font-bold text-green-600">{analytics.completed_tasks || 0}</p>
            </div>
            <div className="p-3 bg-green-100 rounded-xl">
              <CheckCircle className="h-6 w-6 text-green-600" />
            </div>
          </div>
        </Card>

        <Card className="p-6 hover-lift">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">In Progress</p>
              <p className="text-3xl font-bold text-blue-600">{analytics.in_progress_tasks || 0}</p>
            </div>
            <div className="p-3 bg-blue-100 rounded-xl">
              <Clock className="h-6 w-6 text-blue-600" />
            </div>
          </div>
        </Card>

        <Card className="p-6 hover-lift">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Completion Rate</p>
              <p className="text-3xl font-bold text-purple-600">
                {analytics.completion_rate !== undefined 
                  ? `${analytics.completion_rate}%`
                  : (analytics.total_tasks > 0 
                      ? Math.round((analytics.completed_tasks / analytics.total_tasks) * 100)
                      : 0) + '%'}
              </p>
            </div>
            <div className="p-3 bg-purple-100 rounded-xl">
              <TrendingUp className="h-6 w-6 text-purple-600" />
            </div>
          </div>
        </Card>

        <Card className="p-6 hover-lift">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Team Members</p>
              <p className="text-3xl font-bold text-indigo-600">{analytics.team_members || 0}</p>
            </div>
            <div className="p-3 bg-indigo-100 rounded-xl">
              <Users className="h-6 w-6 text-indigo-600" />
            </div>
          </div>
        </Card>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Task Status Distribution */}
        <Card className="p-6">
          <div className="flex items-center gap-3 mb-6">
            <div className="p-2 bg-blue-100 rounded-lg">
              <Users className="h-5 w-5 text-blue-600" />
            </div>
            <h4 className="text-lg font-semibold text-gray-900">Task Status Distribution</h4>
          </div>
          <div className="h-80">
            <Doughnut data={taskStatusData} options={doughnutOptions} />
          </div>
        </Card>

        {/* Priority Distribution */}
        <Card className="p-6">
          <div className="flex items-center gap-3 mb-6">
            <div className="p-2 bg-orange-100 rounded-lg">
              <AlertCircle className="h-5 w-5 text-orange-600" />
            </div>
            <h4 className="text-lg font-semibold text-gray-900">Priority Distribution</h4>
          </div>
          <div className="h-80">
            <Doughnut data={priorityData} options={doughnutOptions} />
          </div>
        </Card>

        {/* Task Status Overview */}
        <Card className="p-6 lg:col-span-2">
          <div className="flex items-center gap-3 mb-6">
            <div className="p-2 bg-green-100 rounded-lg">
              <TrendingUp className="h-5 w-5 text-green-600" />
            </div>
            <h4 className="text-lg font-semibold text-gray-900">Task Status Overview</h4>
          </div>
          <div className="h-80">
            <Bar data={progressData} options={chartOptions} />
          </div>
        </Card>
      </div>

      {/* Additional Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <Card className="p-6 text-center">
          <div className="p-3 bg-yellow-100 rounded-full w-12 h-12 flex items-center justify-center mx-auto mb-3">
            <Clock className="h-6 w-6 text-yellow-600" />
          </div>
          <h5 className="font-semibold text-gray-900 mb-1">Avg. Task Duration</h5>
          <p className="text-2xl font-bold text-yellow-600">
            {formatDuration(analytics.avg_task_duration, analytics.avg_task_duration_minutes)}
          </p>
          {analytics.avg_task_duration_minutes && (
            <p className="text-xs text-gray-500 mt-1">
              {Math.round(analytics.avg_task_duration_minutes)} minutes total
            </p>
          )}
        </Card>

        <Card className="p-6 text-center">
          <div className="p-3 bg-red-100 rounded-full w-12 h-12 flex items-center justify-center mx-auto mb-3">
            <XCircle className="h-6 w-6 text-red-600" />
          </div>
          <h5 className="font-semibold text-gray-900 mb-1">Overdue Tasks</h5>
          <p className="text-2xl font-bold text-red-600">{analytics.overdue_tasks || 0}</p>
        </Card>
      </div>
    </div>
  );
};

export default ProjectAnalytics;
