import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { Link } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { tasksAPI, projectsAPI, authAPI } from '../../services/api';
import Card from '../../components/UI/Card';
import Button from '../../components/UI/Button';
import Input from '../../components/UI/Input';
import Select from '../../components/UI/Select';
import Badge from '../../components/UI/Badge';
import LoadingSpinner from '../../components/UI/LoadingSpinner';
import TaskModal from '../../components/Tasks/TaskModal';
import {
  Plus,
  Search,
  Filter,
  Calendar,
  User,
  AlertCircle,
  CheckCircle,
  Clock,
} from 'lucide-react';
import { format } from 'date-fns';
import toast from 'react-hot-toast';

const Tasks = () => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [showModal, setShowModal] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    priority: '',
    assignee: '',
    project: '',
  });

  // Fetch tasks
  const { data: tasks, isLoading, error } = useQuery(
    ['tasks', filters],
    () => tasksAPI.getTasks(filters),
    {
      keepPreviousData: true,
    }
  );

  // Fetch projects for filter
  const { data: projects } = useQuery('projects', projectsAPI.getProjects);

  // Fetch employees for filter (Scrum Master only)
  const { data: employees } = useQuery(
    'employees',
    () => authAPI.getEmployees(),
    {
      enabled: user?.role === 'scrum_master',
    }
  );

  // Delete task mutation
  const deleteTaskMutation = useMutation(tasksAPI.deleteTask, {
    onSuccess: () => {
      queryClient.invalidateQueries('tasks');
      toast.success('Task deleted successfully');
    },
    onError: (error) => {
      toast.error(error.response?.data?.detail || 'Failed to delete task');
    },
  });

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleEditTask = (task) => {
    setEditingTask(task);
    setShowModal(true);
  };

  const handleDeleteTask = (taskId) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      deleteTaskMutation.mutate(taskId);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'done':
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case 'in_progress':
        return <Clock className="h-4 w-4 text-yellow-600" />;
      default:
        return <AlertCircle className="h-4 w-4 text-gray-600" />;
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      todo: 'gray',
      in_progress: 'warning',
      done: 'success',
    };
    return (
      <Badge variant={variants[status]}>
        {status.replace('_', ' ').toUpperCase()}
      </Badge>
    );
  };

  const getPriorityBadge = (priority) => {
    const variants = {
      low: 'gray',
      medium: 'primary',
      high: 'warning',
      critical: 'danger',
    };
    return (
      <Badge variant={variants[priority]}>
        {priority.toUpperCase()}
      </Badge>
    );
  };

  const statusOptions = [
    { value: '', label: 'All Statuses' },
    { value: 'todo', label: 'To Do' },
    { value: 'in_progress', label: 'In Progress' },
    { value: 'done', label: 'Done' },
  ];

  const priorityOptions = [
    { value: '', label: 'All Priorities' },
    { value: 'low', label: 'Low' },
    { value: 'medium', label: 'Medium' },
    { value: 'high', label: 'High' },
    { value: 'critical', label: 'Critical' },
  ];

  const assigneeOptions = [
    { value: '', label: 'All Assignees' },
    ...(employees?.data || []).map(emp => ({
      value: emp.id,
      label: emp.get_full_name || emp.username,
    })),
  ];

  const projectOptions = [
    { value: '', label: 'All Projects' },
    ...(projects?.data || []).map(project => ({
      value: project.id,
      label: project.name,
    })),
  ];

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Failed to load tasks</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Tasks</h1>
          <p className="text-gray-600">Manage and track your tasks</p>
        </div>
        {user?.role === 'scrum_master' && (
          <Button
            onClick={() => {
              setEditingTask(null);
              setShowModal(true);
            }}
            className="flex items-center gap-2"
          >
            <Plus className="h-4 w-4" />
            New Task
          </Button>
        )}
      </div>

      {/* Filters */}
      <Card className="p-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
          <Input
            placeholder="Search tasks..."
            value={filters.search}
            onChange={(e) => handleFilterChange('search', e.target.value)}
            className="lg:col-span-2"
          />
          <Select
            value={filters.status}
            onChange={(e) => handleFilterChange('status', e.target.value)}
            options={statusOptions}
          />
          <Select
            value={filters.priority}
            onChange={(e) => handleFilterChange('priority', e.target.value)}
            options={priorityOptions}
          />
          {user?.role === 'scrum_master' && (
            <Select
              value={filters.assignee}
              onChange={(e) => handleFilterChange('assignee', e.target.value)}
              options={assigneeOptions}
            />
          )}
          <Select
            value={filters.project}
            onChange={(e) => handleFilterChange('project', e.target.value)}
            options={projectOptions}
          />
        </div>
      </Card>

      {/* Tasks List */}
      <div className="space-y-4">
        {tasks?.data?.results?.length === 0 ? (
          <Card className="p-8 text-center">
            <p className="text-gray-500">No tasks found</p>
          </Card>
        ) : (
          (tasks?.data?.results || []).map((task) => (
            <Card key={task.id} className="p-6 hover:shadow-md transition-shadow">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    {getStatusIcon(task.status)}
                    <Link
                      to={`/tasks/${task.id}`}
                      className="text-lg font-semibold text-gray-900 hover:text-blue-600"
                    >
                      {task.title}
                    </Link>
                    {task.is_overdue && (
                      <Badge variant="danger" size="sm">Overdue</Badge>
                    )}
                  </div>
                  
                  <p className="text-gray-600 mb-3 line-clamp-2">{task.description}</p>
                  
                  <div className="flex items-center gap-4 text-sm text-gray-500">
                    <div className="flex items-center gap-1">
                      <User className="h-4 w-4" />
                      {task.assignee_name || 'Unassigned'}
                    </div>
                    <div className="flex items-center gap-1">
                      <Calendar className="h-4 w-4" />
                      {task.due_date ? format(new Date(task.due_date), 'MMM dd, yyyy') : 'No due date'}
                    </div>
                    <span>Project: {task.project_name}</span>
                  </div>
                </div>
                
                <div className="flex items-center gap-3 ml-4">
                  {getStatusBadge(task.status)}
                  {getPriorityBadge(task.priority)}
                  
                  {user?.role === 'scrum_master' && (
                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleEditTask(task)}
                      >
                        Edit
                      </Button>
                      <Button
                        variant="danger"
                        size="sm"
                        onClick={() => handleDeleteTask(task.id)}
                        loading={deleteTaskMutation.isLoading}
                      >
                        Delete
                      </Button>
                    </div>
                  )}
                </div>
              </div>
            </Card>
          ))
        )}
      </div>

      {/* Task Modal */}
      {showModal && (
        <TaskModal
          task={editingTask}
          onClose={() => {
            setShowModal(false);
            setEditingTask(null);
          }}
          onSuccess={() => {
            setShowModal(false);
            setEditingTask(null);
            queryClient.invalidateQueries('tasks');
          }}
        />
      )}
    </div>
  );
};

export default Tasks;
