import React from 'react';
import { useQuery } from 'react-query';
import { useAuth } from '../../contexts/AuthContext';
import { tasksAPI } from '../../services/api';
import Card from '../../components/UI/Card';
import Badge from '../../components/UI/Badge';
import LoadingSpinner from '../../components/UI/LoadingSpinner';
import {
  CheckSquare,
  Clock,
  AlertCircle,
  Users,
} from 'lucide-react';
import { Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Dashboard = () => {
  const { user } = useAuth();

  const { data: analyticsResp, isLoading, error } = useQuery(
    'taskAnalytics',
    tasksAPI.getTaskAnalytics,
    {
      refetchInterval: 30000, // Refetch every 30 seconds
    }
  );
  const analytics = analyticsResp?.data || analyticsResp;

  // Notifications feed for both roles
  const { data: notifResp } = useQuery(
    'notifications',
    tasksAPI.getNotifications,
    { refetchInterval: 15000 }
  );
  const notifications = notifResp?.data?.items || notifResp?.items || [];

  // For employees: fetch their assigned tasks list (disabled per request)
  const myTasks = null;
  const tasksLoading = false;
  const tasksError = null;

  // Employees: hide assigned projects list on dashboard per request
  const assignedProjects = null;
  const projectsLoading = false;
  const projectsError = null;

  if (isLoading || (user?.role === 'employee' && (projectsLoading || tasksLoading))) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Failed to load dashboard data</p>
      </div>
    );
  }

  if (user?.role === 'employee' && projectsError) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Failed to load assigned projects: {projectsError.message}</p>
      </div>
    );
  }

  if (user?.role === 'employee' && tasksError) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Failed to load your tasks: {tasksError.message}</p>
      </div>
    );
  }

  const stats = [
    {
      name: 'Total Tasks',
      value: analytics?.total_tasks || 0,
      icon: CheckSquare,
      color: 'text-blue-600',
      bgColor: 'bg-blue-50',
    },
    {
      name: 'Completed',
      value: analytics?.completed_tasks || 0,
      icon: CheckSquare,
      color: 'text-green-600',
      bgColor: 'bg-green-50',
    },
    {
      name: 'In Progress',
      value: analytics?.in_progress_tasks || 0,
      icon: Clock,
      color: 'text-yellow-600',
      bgColor: 'bg-yellow-50',
    },
    {
      name: 'Overdue',
      value: analytics?.overdue_tasks || 0,
      icon: AlertCircle,
      color: 'text-red-600',
      bgColor: 'bg-red-50',
    },
  ];

  // Chart data for tasks by status
  const statusChartData = {
    labels: ['To Do', 'In Progress', 'Done'],
    datasets: [
      {
        data: [
          analytics?.todo_tasks || 0,
          analytics?.in_progress_tasks || 0,
          analytics?.completed_tasks || 0,
        ],
        backgroundColor: ['#6b7280', '#f59e0b', '#10b981'],
        borderWidth: 0,
      },
    ],
  };

  const statusChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
      },
    },
  };

  // Chart data for tasks by priority
  const priorityChartData = {
    labels: analytics?.tasks_by_priority?.map(item => 
      item.priority.charAt(0).toUpperCase() + item.priority.slice(1)
    ) || [],
    datasets: [
      {
        label: 'Tasks',
        data: analytics?.tasks_by_priority?.map(item => item.count) || [],
        backgroundColor: ['#ef4444', '#f59e0b', '#3b82f6', '#10b981'],
        borderWidth: 0,
      },
    ],
  };

  const priorityChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 1,
        },
      },
    },
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">
          Welcome back, {user?.first_name || user?.username}!
        </h1>
        <p className="text-gray-600">
          Here's what's happening with your tasks today.
        </p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.name} className="p-6">
              <div className="flex items-center">
                <div className={`p-3 rounded-lg ${stat.bgColor}`}>
                  <Icon className={`h-6 w-6 ${stat.color}`} />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">{stat.name}</p>
                  <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
                </div>
              </div>
            </Card>
          );
        })}
      </div>

      {/* Completion Rate */}
      <Card className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">Completion Rate</h3>
          <Badge variant="success" size="lg">
            {analytics?.completion_rate || 0}%
          </Badge>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div
            className="bg-green-600 h-2 rounded-full transition-all duration-300"
            style={{ width: `${analytics?.completion_rate || 0}%` }}
          ></div>
        </div>
      </Card>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Tasks by Status */}
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Tasks by Status</h3>
          <div className="h-64">
            <Doughnut data={statusChartData} options={statusChartOptions} />
          </div>
        </Card>

        {/* Tasks by Priority */}
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Tasks by Priority</h3>
          <div className="h-64">
            <Bar data={priorityChartData} options={priorityChartOptions} />
          </div>
        </Card>
      </div>

      {/* Recent Activities */}
      {analytics?.recent_activities && analytics.recent_activities.length > 0 && (
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Activities</h3>
          <div className="space-y-3">
            {analytics.recent_activities.slice(0, 5).map((activity, index) => (
              <div key={index} className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                <div className="flex-shrink-0">
                  <div className="w-2 h-2 bg-blue-600 rounded-full"></div>
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm text-gray-900">{activity.description}</p>
                  <p className="text-xs text-gray-500">
                    by {activity.user_name} • {new Date(activity.created_at).toLocaleString()}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}

      {/* Notifications (Both roles) */}
      {notifications.length > 0 && (
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Notifications</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {notifications.slice(0, 12).map((n) => (
              <div
                key={`${n.type}-${n.id}`}
                className="p-4 rounded-lg border border-gray-200 bg-white hover:shadow-sm transition"
              >
                <div className="flex items-start justify-between">
                  <span className={`text-xs px-2 py-0.5 rounded-full ${n.type === 'activity' ? 'bg-blue-100 text-blue-700' : 'bg-green-100 text-green-700'}`}>
                    {n.type === 'activity' ? 'Activity' : 'Message'}
                  </span>
                  <span className="text-xs text-gray-500">{new Date(n.created_at).toLocaleString()}</span>
                </div>
                <div className="mt-2 text-sm text-gray-900">
                  {n.type === 'activity' ? (
                    <>
                      <p className="font-medium line-clamp-2">{n.description}</p>
                      {n.task_title && (
                        <p className="text-xs text-gray-500 mt-1">Task: {n.task_title}</p>
                      )}
                    </>
                  ) : (
                    <>
                      <p className="font-medium line-clamp-2">{n.content}</p>
                      <p className="text-xs text-gray-500 mt-1">From: {n.author_name}{n.project_name ? ` • ${n.project_name}` : ''}</p>
                    </>
                  )}
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}

      {/* Assigned Projects (Employee only) - hidden per request */}

      {/* My Tasks (Employee only) - hidden per request */}

      {/* Tasks by Assignee (Scrum Master only) */}
      {user?.role === 'scrum_master' && analytics?.tasks_by_assignee && analytics.tasks_by_assignee.length > 0 && (
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Tasks by Team Member</h3>
          <div className="space-y-4">
            {analytics.tasks_by_assignee.map((member, index) => (
              <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                    <Users className="h-5 w-5 text-blue-600" />
                  </div>
                  <div>
                    <p className="font-medium text-gray-900">
                      {member.assignee__first_name} {member.assignee__last_name}
                    </p>
                    <p className="text-sm text-gray-500">
                      {member.total} total tasks
                    </p>
                  </div>
                </div>
                <div className="flex space-x-2">
                  <Badge variant="gray">{member.todo} To Do</Badge>
                  <Badge variant="warning">{member.in_progress} In Progress</Badge>
                  <Badge variant="success">{member.completed} Done</Badge>
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}
    </div>
  );
};

export default Dashboard;
