import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { useAuth } from '../../contexts/AuthContext';
import { projectsAPI } from '../../services/api';
import Card from '../../components/UI/Card';
import Button from '../../components/UI/Button';
import Input from '../../components/UI/Input';
import Textarea from '../../components/UI/Textarea';
import Badge from '../../components/UI/Badge';
import LoadingSpinner from '../../components/UI/LoadingSpinner';
import ProjectMembersModal from '../../components/Projects/ProjectMembersModal';
import ProjectChat from '../../components/Projects/ProjectChat';
import KanbanBoard from '../../components/Tasks/KanbanBoard';
import TaskModal from '../../components/Tasks/TaskModal';
import {
  Plus,
  FolderOpen,
  Calendar,
  User,
  BarChart3,
  Edit,
  Trash2,
  Users,
  TrendingUp,
  MessageSquare,
  Send,
  UserPlus,
} from 'lucide-react';
import { format } from 'date-fns';
import toast from 'react-hot-toast';

const Projects = () => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [showModal, setShowModal] = useState(false);
  const [editingProject, setEditingProject] = useState(null);
  const [selectedProject, setSelectedProject] = useState(null);
  const [showMessages, setShowMessages] = useState(false);
  const [showMembers, setShowMembers] = useState(false);
  const [showKanban, setShowKanban] = useState(false);
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [newMessage, setNewMessage] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    description: '',
  });

  // Fetch projects - different API based on user role
  const { data: projects, isLoading, error } = useQuery(
    'projects',
    user?.role === 'employee' ? projectsAPI.getMyProjects : projectsAPI.getProjects,
    {
      onError: (error) => {
        console.error('Error fetching projects:', error);
      },
      onSuccess: (data) => {
        console.log('Projects data:', data);
        console.log('User role:', user?.role);
      }
    }
  );

  // Fetch project messages when a project is selected
  const { data: messages, isLoading: messagesLoading, refetch: refetchMessages } = useQuery(
    ['projectMessages', selectedProject?.id],
    () => projectsAPI.getProjectMessages(selectedProject.id),
    {
      enabled: !!selectedProject && showMessages,
      refetchInterval: 5000, // Refetch every 5 seconds for real-time updates
    }
  );

  // Create/Update project mutation
  const projectMutation = useMutation(
    (data) => editingProject ? projectsAPI.updateProject(editingProject.id, data) : projectsAPI.createProject(data),
    {
      onSuccess: () => {
        queryClient.invalidateQueries('projects');
        setShowModal(false);
        setEditingProject(null);
        setFormData({ name: '', description: '' });
        toast.success(editingProject ? 'Project updated successfully' : 'Project created successfully');
      },
      onError: (error) => {
        const data = error.response?.data;
        let message = 'Operation failed';
        if (typeof data === 'string') {
          message = data;
        } else if (data?.detail) {
          message = data.detail;
        } else if (data && typeof data === 'object') {
          const first = Object.entries(data)[0];
          if (first) {
            const [, msgs] = first;
            message = Array.isArray(msgs) ? msgs[0] : String(msgs);
          }
        }
        toast.error(message);
      },
    }
  );

  // Delete project mutation
  const deleteProjectMutation = useMutation(
    (projectId) => projectsAPI.deleteProject(projectId),
    {
      onSuccess: () => {
        queryClient.invalidateQueries('projects');
        toast.success('Project deleted successfully');
      },
      onError: (error) => {
        toast.error(error.response?.data?.detail || 'Failed to delete project');
      },
    }
  );

  // Send message mutation
  const sendMessageMutation = useMutation(
    (messageData) => projectsAPI.createProjectMessage(selectedProject.id, messageData),
    {
      onSuccess: () => {
        setNewMessage('');
        refetchMessages();
        toast.success('Message sent');
      },
      onError: (error) => {
        toast.error(error.response?.data?.detail || 'Failed to send message');
      },
    }
  );

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name.trim()) {
      toast.error('Project name is required');
      return;
    }
    const payload = {
      name: formData.name.trim(),
      description: (formData.description || '').trim(),
    };
    projectMutation.mutate(payload);
  };

  const handleEditProject = (project) => {
    setEditingProject(project);
    setFormData({
      name: project.name,
      description: project.description,
    });
    setShowModal(true);
  };

  const handleDeleteProject = (projectId) => {
    if (window.confirm('Are you sure you want to delete this project? This will also delete all associated tasks.')) {
      deleteProjectMutation.mutate(projectId);
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;
    
    sendMessageMutation.mutate({
      content: newMessage.trim(),
    });
  };

  const handleOpenMessages = (project) => {
    setSelectedProject(project);
    setShowMessages(true);
  };

  const handleCloseMessages = () => {
    setShowMessages(false);
    setSelectedProject(null);
    setNewMessage('');
  };

  const handleOpenMembers = (project) => {
    setSelectedProject(project);
    setShowMembers(true);
  };

  const handleCloseMembers = () => {
    setShowMembers(false);
    setSelectedProject(null);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Failed to load projects</p>
      </div>
    );
  }

  // Normalize paginated/non-paginated responses
  const projectList = Array.isArray(projects)
    ? projects
    : (Array.isArray(projects?.data) 
        ? projects.data 
        : (projects?.data?.results || []));

  // Debug logging
  console.log('Projects response:', projects);
  console.log('Project list:', projectList);
  console.log('Project list length:', projectList.length);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Projects</h1>
          <p className="text-gray-600">Manage your projects and track progress</p>
        </div>
        {user?.role === 'scrum_master' && (
          <Button
            onClick={() => {
              setEditingProject(null);
              setFormData({ name: '', description: '' });
              setShowModal(true);
            }}
            className="flex items-center gap-2"
          >
            <Plus className="h-4 w-4" />
            New Project
          </Button>
        )}
      </div>

      {/* Projects Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {projectList.length === 0 ? (
          <div className="col-span-full">
            <Card className="p-8 text-center">
              <FolderOpen className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500">
                {user?.role === 'employee' 
                  ? 'No projects assigned to you yet. Contact your Scrum Master to be added to a project.'
                  : 'No projects found'
                }
              </p>
              {user?.role === 'scrum_master' && (
                <Button
                  onClick={() => setShowModal(true)}
                  className="mt-4"
                >
                  Create your first project
                </Button>
              )}
            </Card>
          </div>
        ) : (
          projectList.map((project) => (
            <Card key={project.id} className="p-6 hover:shadow-md transition-shadow">
              <div className="space-y-4">
                {/* Project Header */}
                <div className="flex items-start justify-between">
                  <div className="flex items-center gap-3">
                    <div className="p-2 bg-blue-100 rounded-lg">
                      <FolderOpen className="h-5 w-5 text-blue-600" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900">{project.name}</h3>
                      <Badge variant="primary" size="sm">
                        {project.task_count} tasks
                      </Badge>
                    </div>
                  </div>
                  
                  {user?.role === 'scrum_master' && (
                    <div className="flex items-center gap-1">
                      <button
                        onClick={() => handleEditProject(project)}
                        className="text-gray-400 hover:text-gray-600"
                      >
                        <Edit className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => handleDeleteProject(project.id)}
                        className="text-gray-400 hover:text-red-600"
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                    </div>
                  )}
                </div>

                {/* Project Description */}
                {project.description && (
                  <p className="text-gray-600 text-sm line-clamp-3">
                    {project.description}
                  </p>
                )}

                {/* Project Meta */}
                <div className="space-y-2 text-sm text-gray-500">
                  <div className="flex items-center gap-2">
                    <User className="h-4 w-4" />
                    Created by {project.created_by_name}
                  </div>
                  <div className="flex items-center gap-2">
                    <Calendar className="h-4 w-4" />
                    {format(new Date(project.created_at), 'MMM dd, yyyy')}
                  </div>
                  
                  {/* Member count and progress */}
                  <div className="flex items-center justify-between pt-2 border-t">
                    <div className="flex items-center gap-1">
                      <Users className="h-4 w-4" />
                      <span>{project.member_count} members</span>
                    </div>
                    <div className="flex items-center gap-1">
                      <TrendingUp className="h-4 w-4" />
                      <span>{project.progress_percentage}% complete</span>
                    </div>
                  </div>
                  
                  {/* Progress bar */}
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div
                      className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${project.progress_percentage}%` }}
                    ></div>
                  </div>
                </div>

                {/* Project Actions */}
                <div className="flex items-center gap-2 pt-2 border-t">
                  <Button
                    variant="outline"
                    size="sm"
                    className="flex-1"
                    onClick={() => {
                      // Navigate to tasks filtered by project
                      window.location.href = `/tasks?project=${project.id}`;
                    }}
                  >
                    View Tasks
                  </Button>
                  
                  {/* Chat button for project members */}
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleOpenMessages(project)}
                    className="flex items-center gap-1"
                  >
                    <MessageSquare className="h-4 w-4" />
                    Chat
                  </Button>
                  
                  {user?.role === 'scrum_master' && (
                    <>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleOpenMembers(project)}
                        className="flex items-center gap-1"
                      >
                        <UserPlus className="h-4 w-4" />
                        Members
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          // Navigate to project analytics
                          window.location.href = `/projects/${project.id}/analytics`;
                        }}
                      >
                        <BarChart3 className="h-4 w-4" />
                      </Button>
                    </>
                  )}
                </div>
              </div>
            </Card>
          ))
        )}
      </div>

      {/* Project Modal */}
      {showModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" onClick={() => setShowModal(false)}></div>

            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <form onSubmit={handleSubmit}>
                <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-lg font-semibold text-gray-900">
                      {editingProject ? 'Edit Project' : 'Create New Project'}
                    </h3>
                    <button
                      type="button"
                      onClick={() => setShowModal(false)}
                      className="text-gray-400 hover:text-gray-600"
                    >
                      
                    </button>
                  </div>

                  <div className="space-y-4">
                    <Input
                      label="Project Name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      required
                      placeholder="Enter project name"
                    />

                    <Textarea
                      label="Description"
                      name="description"
                      value={formData.description}
                      onChange={handleInputChange}
                      placeholder="Enter project description"
                      rows={3}
                    />
                  </div>
                </div>

                <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                  <Button
                    type="submit"
                    loading={projectMutation.isLoading}
                    disabled={projectMutation.isLoading}
                    className="w-full sm:w-auto sm:ml-3"
                  >
                    {editingProject ? 'Update Project' : 'Create Project'}
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setShowModal(false)}
                    className="mt-3 w-full sm:mt-0 sm:w-auto"
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Project Messages Modal */}
      {showMessages && selectedProject && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" onClick={handleCloseMessages}></div>

            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-2xl sm:w-full">
              <div className="bg-white">
                {/* Header */}
                <div className="px-6 py-4 border-b border-gray-200">
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">
                        {selectedProject.name} - Project Chat
                      </h3>
                      <p className="text-sm text-gray-500">
                        {selectedProject.member_count} members  {selectedProject.progress_percentage}% complete
                      </p>
                    </div>
                    <button
                      onClick={handleCloseMessages}
                      className="text-gray-400 hover:text-gray-600"
                    >
                      
                    </button>
                  </div>
                </div>

                {/* Messages */}
                <div className="px-6 py-4 h-96 overflow-y-auto bg-gray-50">
                  {messagesLoading ? (
                    <div className="flex items-center justify-center h-full">
                      <LoadingSpinner size="md" />
                    </div>
                  ) : messages && messages.length > 0 ? (
                    <div className="space-y-4">
                      {messages.map((message) => (
                        <div key={message.id} className="flex items-start space-x-3">
                          <div className="flex-shrink-0">
                            <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                              <User className="h-4 w-4 text-blue-600" />
                            </div>
                          </div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center space-x-2">
                              <p className="text-sm font-medium text-gray-900">
                                {message.author_name}
                              </p>
                              <Badge variant="gray" size="sm">
                                {message.author_role}
                              </Badge>
                              <p className="text-xs text-gray-500">
                                {format(new Date(message.created_at), 'MMM dd, HH:mm')}
                              </p>
                            </div>
                            <p className="text-sm text-gray-700 mt-1">
                              {message.content}
                            </p>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8">
                      <MessageSquare className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                      <p className="text-gray-500">No messages yet</p>
                      <p className="text-sm text-gray-400">Start the conversation!</p>
                    </div>
                  )}
                </div>

                {/* Message Input */}
                <div className="px-6 py-4 border-t border-gray-200">
                  <form onSubmit={handleSendMessage} className="flex items-center space-x-3">
                    <Input
                      value={newMessage}
                      onChange={(e) => setNewMessage(e.target.value)}
                      placeholder="Type your message..."
                      className="flex-1"
                      disabled={sendMessageMutation.isLoading}
                    />
                    <Button
                      type="submit"
                      size="sm"
                      loading={sendMessageMutation.isLoading}
                      disabled={!newMessage.trim() || sendMessageMutation.isLoading}
                      className="flex items-center gap-2"
                    >
                      <Send className="h-4 w-4" />
                      Send
                    </Button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Project Members Modal */}
      <ProjectMembersModal
        project={selectedProject}
        isOpen={showMembers}
        onClose={handleCloseMembers}
      />
    </div>
  );
};

export default Projects;
