import React, { useState, useEffect } from 'react';
import { useMutation, useQuery } from 'react-query';
import { useAuth } from '../../contexts/AuthContext';
import { tasksAPI, projectsAPI, authAPI } from '../../services/api';
import Button from '../UI/Button';
import Input from '../UI/Input';
import Select from '../UI/Select';
import Textarea from '../UI/Textarea';
import { X } from 'lucide-react';
import toast from 'react-hot-toast';

const TaskModal = ({ task, onClose, onSuccess }) => {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    project: '',
    assignee: '',
    assignee_ids: [],
    priority: 'medium',
    status: 'todo',
    due_date: '',
  });
  const [errors, setErrors] = useState({});

  // Fetch projects and employees
  const { data: projects } = useQuery('projects', projectsAPI.getProjects);
  const { data: employees } = useQuery(
    'employees',
    () => authAPI.getEmployees(),
    {
      enabled: user?.role === 'scrum_master',
    }
  );

  // Create/Update task mutation
  const taskMutation = useMutation(
    (data) => task ? tasksAPI.updateTask(task.id, data) : tasksAPI.createTask(data),
    {
      onSuccess: () => {
        toast.success(task ? 'Task updated successfully' : 'Task created successfully');
        onSuccess();
      },
      onError: (error) => {
        const message = error.response?.data?.detail || 'Operation failed';
        toast.error(message);
      },
    }
  );

  useEffect(() => {
    if (task) {
      setFormData({
        title: task.title || '',
        description: task.description || '',
        project: task.project || '',
        assignee: task.assignee || '',
        assignee_ids: task.assignees ? task.assignees.map(a => a.user) : [],
        priority: task.priority || 'medium',
        status: task.status || 'todo',
        due_date: task.due_date ? new Date(task.due_date).toISOString().split('T')[0] : '',
      });
    }
  }, [task]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const handleAssigneeChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, option => parseInt(option.value));
    setFormData(prev => ({
      ...prev,
      assignee_ids: selectedOptions,
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }

    if (!formData.project) {
      newErrors.project = 'Project is required';
    }

    if (user?.role === 'scrum_master' && !formData.assignee) {
      newErrors.assignee = 'Assignee is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    const submitData = {
      ...formData,
      project: formData.project ? parseInt(formData.project, 10) : null,
      assignee: formData.assignee ? parseInt(formData.assignee, 10) : null,
      due_date: formData.due_date || null,
    };

    taskMutation.mutate(submitData);
  };

  const priorityOptions = [
    { value: 'low', label: 'Low' },
    { value: 'medium', label: 'Medium' },
    { value: 'high', label: 'High' },
    { value: 'critical', label: 'Critical' },
  ];

  const statusOptions = [
    { value: 'todo', label: 'To Do' },
    { value: 'in_progress', label: 'In Progress' },
    { value: 'done', label: 'Done' },
  ];

  const projectArray = Array.isArray(projects?.data)
    ? projects.data
    : (projects?.data?.results || []);

  const projectOptions = projectArray.map(project => ({
    value: project.id,
    label: project.name,
  }));

  const assigneeOptions = (employees?.data || []).map(emp => ({
    value: emp.id,
    label: emp.get_full_name || emp.username,
  }));

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" onClick={onClose}></div>

        <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
          <form onSubmit={handleSubmit}>
            <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900">
                  {task ? 'Edit Task' : 'Create New Task'}
                </h3>
                <button
                  type="button"
                  onClick={onClose}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>

              <div className="space-y-4">
                <Input
                  label="Title"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  error={errors.title}
                  required
                  placeholder="Enter task title"
                />

                <Textarea
                  label="Description"
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  placeholder="Enter task description"
                  rows={3}
                />

                <Select
                  label="Project"
                  name="project"
                  value={formData.project}
                  onChange={handleChange}
                  options={projectOptions}
                  error={errors.project}
                  required
                  placeholder="Select project"
                />

                {user?.role === 'scrum_master' && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Assignees (Select multiple)
                    </label>
                    <select
                      multiple
                      value={formData.assignee_ids}
                      onChange={handleAssigneeChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                      size="4"
                    >
                      {employees?.map((employee) => (
                        <option key={employee.id} value={employee.id}>
                          {employee.first_name} {employee.last_name} ({employee.email})
                        </option>
                      ))}
                    </select>
                    <p className="mt-1 text-sm text-gray-500">
                      Hold Ctrl (or Cmd on Mac) to select multiple assignees
                    </p>
                  </div>
                )}

                <div className="grid grid-cols-2 gap-4">
                  <Select
                    label="Priority"
                    name="priority"
                    value={formData.priority}
                    onChange={handleChange}
                    options={priorityOptions}
                  />

                  <Select
                    label="Status"
                    name="status"
                    value={formData.status}
                    onChange={handleChange}
                    options={statusOptions}
                  />
                </div>

                <Input
                  label="Due Date"
                  name="due_date"
                  type="date"
                  value={formData.due_date}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
              <Button
                type="submit"
                loading={taskMutation.isLoading}
                disabled={taskMutation.isLoading}
                className="w-full sm:w-auto sm:ml-3"
              >
                {task ? 'Update Task' : 'Create Task'}
              </Button>
              <Button
                type="button"
                variant="outline"
                onClick={onClose}
                className="mt-3 w-full sm:mt-0 sm:w-auto"
              >
                Cancel
              </Button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default TaskModal;
