import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { useAuth } from '../../contexts/AuthContext';
import { tasksAPI, projectsAPI } from '../../services/api';
import Card from '../../components/UI/Card';
import Badge from '../../components/UI/Badge';
import LoadingSpinner from '../../components/UI/LoadingSpinner';
import {
  CheckSquare,
  Clock,
  AlertCircle,
  Users,
} from 'lucide-react';
import { Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Dashboard = () => {
  const { user } = useAuth();

  const [selectedProjectId, setSelectedProjectId] = useState('');

  const { data: smProjects } = useQuery(
    'smProjects',
    projectsAPI.getProjects,
    { enabled: user?.role === 'scrum_master' }
  );

  // Employee: fetch assigned projects for dashboard
  const { data: assignedProjectsResp, isLoading: assignedLoading, error: assignedError } = useQuery(
    'assignedProjects',
    projectsAPI.getAssignedProjects,
    { enabled: user?.role === 'employee' }
  );

  const { data: analyticsResp, isLoading, error } = useQuery(
    ['taskAnalytics', selectedProjectId, user?.role],
    async () => {
      if (selectedProjectId) {
        try {
          const res = await projectsAPI.getProjectAnalytics(selectedProjectId);
          return res.data || res;
        } catch (e) {
          // Fallback to overall analytics if project analytics is unavailable/forbidden
          if (e?.response?.status === 403 || e?.response?.status === 404) {
            const res = await tasksAPI.getTaskAnalytics();
            return res.data || res;
          }
          throw e;
        }
      }
      const res = await tasksAPI.getTaskAnalytics();
      return res.data || res;
    },
    { refetchInterval: 30000, retry: false }
  );
  const analytics = analyticsResp;

  // Notifications are shown via the header bell; dashboard feed removed per request

  // For employees: placeholder for tasks list (kept disabled)
  const myTasks = null;
  const tasksLoading = false;
  const tasksError = null;

  // Normalize assigned projects for employees
  const assignedProjects = Array.isArray(assignedProjectsResp?.data)
    ? assignedProjectsResp.data
    : (assignedProjectsResp?.data?.results || []);

  if (isLoading || (user?.role === 'employee' && (assignedLoading || tasksLoading))) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Failed to load dashboard data</p>
      </div>
    );
  }

  if (user?.role === 'employee' && assignedError) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Failed to load assigned projects: {assignedError.message}</p>
      </div>
    );
  }

  if (user?.role === 'employee' && tasksError) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Failed to load your tasks: {tasksError.message}</p>
      </div>
    );
  }

  const stats = [
    {
      name: 'Total Tasks',
      value: analytics?.total_tasks || 0,
      icon: CheckSquare,
      color: 'text-blue-600',
      bgColor: 'bg-blue-50',
    },
    {
      name: 'Completed',
      value: analytics?.completed_tasks || 0,
      icon: CheckSquare,
      color: 'text-green-600',
      bgColor: 'bg-green-50',
    },
    {
      name: 'In Progress',
      value: analytics?.in_progress_tasks || 0,
      icon: Clock,
      color: 'text-yellow-600',
      bgColor: 'bg-yellow-50',
    },
    {
      name: 'Overdue',
      value: analytics?.overdue_tasks || 0,
      icon: AlertCircle,
      color: 'text-red-600',
      bgColor: 'bg-red-50',
    },
  ];

  // Chart data for tasks by status
  const statusChartData = {
    labels: ['To Do', 'In Progress', 'Done'],
    datasets: [
      {
        data: [
          analytics?.todo_tasks || 0,
          analytics?.in_progress_tasks || 0,
          analytics?.completed_tasks || 0,
        ],
        backgroundColor: ['#6b7280', '#f59e0b', '#10b981'],
        borderWidth: 0,
      },
    ],
  };

  const statusChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
      },
    },
  };

  // Chart data for tasks by priority
  const priorityItems = Array.isArray(analytics?.tasks_by_priority) ? analytics.tasks_by_priority : [];
  const priorityChartData = {
    labels: priorityItems.map(item => (item.priority || '').toString().charAt(0).toUpperCase() + (item.priority || '').toString().slice(1)),
    datasets: [
      {
        label: 'Tasks',
        data: priorityItems.map(item => item.count || 0),
        backgroundColor: ['#ef4444', '#f59e0b', '#3b82f6', '#10b981'],
        borderWidth: 0,
      },
    ],
  };

  const priorityChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 1,
        },
      },
    },
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">
          Welcome back, {user?.first_name || user?.username}!
        </h1>
        <p className="text-gray-600">
          Here's what's happening with your tasks today.
        </p>
      </div>

      {/* Project selection handled within Projects Overview cards */}

      {/* Scrum Master: All projects quick cards */}
      {user?.role === 'scrum_master' && (
        <Card className="p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">Projects Overview</h3>
            <div className="flex items-center gap-2">
              <label className="text-sm text-gray-700">Select project</label>
              <select
                className="border border-gray-300 rounded-md px-3 py-2 text-sm"
                value={selectedProjectId}
                onChange={(e) => setSelectedProjectId(e.target.value)}
              >
                <option value="">All projects</option>
                {(() => {
                  const list = Array.isArray(smProjects?.data) ? smProjects.data : (smProjects?.data?.results || []);
                  return list.map((p) => (
                    <option key={p.id} value={p.id}>{p.name}</option>
                  ));
                })()}
              </select>
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {(() => {
              const list = Array.isArray(smProjects?.data) ? smProjects.data : (smProjects?.data?.results || []);
              if (!list.length) {
                return (
                  <div className="col-span-full text-center text-gray-500 py-6">No projects yet</div>
                );
              }
              return list.map((p) => (
                <div key={p.id} className={`p-4 rounded-xl border ${String(selectedProjectId) === String(p.id) ? 'border-blue-500 bg-blue-50' : 'border-gray-200 bg-white'} shadow-sm hover:shadow transition`}> 
                  <div className="flex items-start justify-between">
                    <div>
                      <h4 className="font-semibold text-gray-900">{p.name}</h4>
                      {p.description && (
                        <p className="text-xs text-gray-500 mt-1 line-clamp-2">{p.description}</p>
                      )}
                    </div>
                    <span className="text-xs px-2 py-0.5 rounded-full bg-gray-100 text-gray-700">{p.task_count || 0} tasks</span>
                  </div>
                  <div className="mt-3 flex items-center justify-between text-sm text-gray-700">
                    <span>{p.member_count || 0} members</span>
                    <span>{p.progress_percentage || 0}% complete</span>
                  </div>
                  <div className="mt-2 w-full bg-gray-200 rounded-full h-2">
                    <div className="h-2 rounded-full bg-gradient-to-r from-blue-500 to-green-500" style={{ width: `${p.progress_percentage || 0}%` }} />
                  </div>
                  <div className="mt-4 flex items-center gap-2">
                    <button
                      onClick={() => setSelectedProjectId(String(p.id))}
                      className={`flex-1 text-sm font-medium px-3 py-2 rounded-md ${String(selectedProjectId) === String(p.id) ? 'bg-blue-600 text-white' : 'bg-blue-50 text-blue-700 hover:bg-blue-100'}`}
                    >
                      View Analytics
                    </button>
                    <button
                      onClick={() => window.location.assign('/projects')}
                      className="text-sm px-3 py-2 rounded-md border border-gray-200 text-gray-700 hover:bg-gray-50"
                    >
                      Manage
                    </button>
                  </div>
                </div>
              ));
            })()}
          </div>
        </Card>
      )}

      {/* Employee: My Projects section removed per request */}

      {/* Employee: Projects Overview with selector */}
      {user?.role === 'employee' && (
        <Card className="p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">Projects Overview</h3>
            <div className="flex items-center gap-2">
              <label className="text-sm text-gray-700">Select project</label>
              <select
                className="border border-gray-300 rounded-md px-3 py-2 text-sm"
                value={selectedProjectId}
                onChange={(e) => setSelectedProjectId(e.target.value)}
              >
                <option value="">All projects</option>
                {assignedProjects.map((p) => (
                  <option key={p.id} value={p.id}>{p.name}</option>
                ))}
              </select>
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {assignedProjects.length === 0 ? (
              <div className="col-span-full text-center text-gray-500 py-6">No projects yet</div>
            ) : (
              assignedProjects.map((p) => (
                <div key={p.id} className={`p-4 rounded-xl border ${String(selectedProjectId) === String(p.id) ? 'border-blue-500 bg-blue-50' : 'border-gray-200 bg-white'} shadow-sm hover:shadow transition`}>
                  <div className="flex items-start justify-between">
                    <div>
                      <h4 className="font-semibold text-gray-900">{p.name}</h4>
                      {p.description && (
                        <p className="text-xs text-gray-500 mt-1 line-clamp-2">{p.description}</p>
                      )}
                    </div>
                    <span className="text-xs px-2 py-0.5 rounded-full bg-gray-100 text-gray-700">{p.task_count || 0} tasks</span>
                  </div>
                  <div className="mt-3 flex items-center justify-between text-sm text-gray-700">
                    <span>{p.member_count || 0} members</span>
                    <span>{p.progress_percentage || 0}% complete</span>
                  </div>
                  <div className="mt-2 w-full bg-gray-200 rounded-full h-2">
                    <div className="h-2 rounded-full bg-gradient-to-r from-blue-500 to-green-500" style={{ width: `${p.progress_percentage || 0}%` }} />
                  </div>
                  <div className="mt-4 flex items-center gap-2">
                    <button
                      onClick={() => setSelectedProjectId(String(p.id))}
                      className={`flex-1 text-sm font-medium px-3 py-2 rounded-md ${String(selectedProjectId) === String(p.id) ? 'bg-blue-600 text-white' : 'bg-blue-50 text-blue-700 hover:bg-blue-100'}`}
                    >
                      View Analytics
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        </Card>
      )}

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.name} className="p-6">
              <div className="flex items-center">
                <div className={`p-3 rounded-lg ${stat.bgColor}`}>
                  <Icon className={`h-6 w-6 ${stat.color}`} />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">{stat.name}</p>
                  <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
                </div>
              </div>
            </Card>
          );
        })}
      </div>

      {/* Completion Rate */}
      <Card className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">Completion Rate</h3>
          <Badge variant="success" size="lg">
            {analytics?.completion_rate || 0}%
          </Badge>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div
            className="bg-green-600 h-2 rounded-full transition-all duration-300"
            style={{ width: `${analytics?.completion_rate || 0}%` }}
          ></div>
        </div>
      </Card>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Tasks by Status */}
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Tasks by Status</h3>
          <div className="h-64">
            <Doughnut data={statusChartData} options={statusChartOptions} />
          </div>
        </Card>

        {/* Tasks by Priority */}
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Tasks by Priority</h3>
          <div className="h-64">
            <Bar data={priorityChartData} options={priorityChartOptions} />
          </div>
        </Card>
      </div>

      {/* Recent Activities */}
      {analytics?.recent_activities && analytics.recent_activities.length > 0 && (
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Activities</h3>
          <div className="space-y-3">
            {analytics.recent_activities.slice(0, 5).map((activity, index) => (
              <div key={index} className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                <div className="flex-shrink-0">
                  <div className="w-2 h-2 bg-blue-600 rounded-full"></div>
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm text-gray-900">{activity.description}</p>
                  <p className="text-xs text-gray-500">
                    by {activity.user_name}  {new Date(activity.created_at).toLocaleString()}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}

      {/* Notifications feed removed; use bell icon in header */}

      {/* Assigned Projects (Employee only) - hidden per request */}

      {/* My Tasks (Employee only) - hidden per request */}

      {/* Tasks by Assignee (Scrum Master only) */}
      {user?.role === 'scrum_master' && analytics?.tasks_by_assignee && analytics.tasks_by_assignee.length > 0 && (
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Tasks by Team Member</h3>
          <div className="space-y-4">
            {analytics.tasks_by_assignee.map((member, index) => (
              <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                    <Users className="h-5 w-5 text-blue-600" />
                  </div>
                  <div>
                    <p className="font-medium text-gray-900">
                      {member.assignee__first_name} {member.assignee__last_name}
                    </p>
                    <p className="text-sm text-gray-500">
                      {member.total} total tasks
                    </p>
                  </div>
                </div>
                <div className="flex space-x-2">
                  <Badge variant="gray">{member.todo} To Do</Badge>
                  <Badge variant="warning">{member.in_progress} In Progress</Badge>
                  <Badge variant="success">{member.completed} Done</Badge>
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}
    </div>
  );
};

export default Dashboard;
