import React, { useState } from 'react';
import { Plus, FileText, Edit, Trash2, Copy, CheckCircle } from 'lucide-react';
import Card from '../UI/Card';
import Button from '../UI/Button';

const TaskTemplateManager = ({ onTemplateSelect, onTemplateCreate }) => {
  const [templates, setTemplates] = useState([
    {
      id: 1,
      name: 'Bug Fix Template',
      description: 'Standard template for bug fixes',
      tasks: [
        { title: 'Reproduce the bug', description: 'Identify steps to reproduce the issue', priority: 'high' },
        { title: 'Analyze root cause', description: 'Investigate the underlying cause', priority: 'high' },
        { title: 'Implement fix', description: 'Code the solution', priority: 'medium' },
        { title: 'Write tests', description: 'Add unit tests for the fix', priority: 'medium' },
        { title: 'Test fix', description: 'Verify the fix works correctly', priority: 'high' },
        { title: 'Update documentation', description: 'Update relevant documentation', priority: 'low' }
      ],
      category: 'Development'
    },
    {
      id: 2,
      name: 'Feature Development',
      description: 'Template for new feature development',
      tasks: [
        { title: 'Requirements analysis', description: 'Gather and analyze requirements', priority: 'high' },
        { title: 'Design architecture', description: 'Design system architecture', priority: 'high' },
        { title: 'Create wireframes', description: 'Design user interface wireframes', priority: 'medium' },
        { title: 'Database design', description: 'Design database schema', priority: 'medium' },
        { title: 'Backend development', description: 'Implement backend functionality', priority: 'high' },
        { title: 'Frontend development', description: 'Implement user interface', priority: 'high' },
        { title: 'Integration testing', description: 'Test integration between components', priority: 'medium' },
        { title: 'User acceptance testing', description: 'Conduct UAT with stakeholders', priority: 'high' },
        { title: 'Deployment', description: 'Deploy to production', priority: 'high' }
      ],
      category: 'Development'
    },
    {
      id: 3,
      name: 'Code Review Process',
      description: 'Template for code review workflow',
      tasks: [
        { title: 'Self review', description: 'Review own code before submission', priority: 'medium' },
        { title: 'Create pull request', description: 'Create PR with detailed description', priority: 'high' },
        { title: 'Assign reviewers', description: 'Assign appropriate team members', priority: 'medium' },
        { title: 'Address feedback', description: 'Address reviewer comments', priority: 'high' },
        { title: 'Final approval', description: 'Get final approval from reviewers', priority: 'high' },
        { title: 'Merge to main', description: 'Merge approved changes', priority: 'medium' }
      ],
      category: 'Process'
    },
    {
      id: 4,
      name: 'Database Migration',
      description: 'Template for database migration tasks',
      tasks: [
        { title: 'Backup current database', description: 'Create full backup before migration', priority: 'high' },
        { title: 'Create migration script', description: 'Write migration SQL script', priority: 'high' },
        { title: 'Test migration on staging', description: 'Test migration on staging environment', priority: 'high' },
        { title: 'Schedule maintenance window', description: 'Plan maintenance window for migration', priority: 'medium' },
        { title: 'Execute migration', description: 'Run migration in production', priority: 'high' },
        { title: 'Verify data integrity', description: 'Verify data after migration', priority: 'high' },
        { title: 'Update application config', description: 'Update application configuration', priority: 'medium' }
      ],
      category: 'DevOps'
    }
  ]);

  const [selectedTemplate, setSelectedTemplate] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);

  const handleTemplateSelect = (template) => {
    setSelectedTemplate(template);
    if (onTemplateSelect) {
      onTemplateSelect(template);
    }
  };

  const handleCreateTemplate = (templateData) => {
    const newTemplate = {
      id: Date.now(),
      ...templateData,
      createdAt: new Date().toISOString()
    };
    setTemplates([...templates, newTemplate]);
    setShowCreateForm(false);
    if (onTemplateCreate) {
      onTemplateCreate(newTemplate);
    }
  };

  const handleDeleteTemplate = (templateId) => {
    setTemplates(templates.filter(t => t.id !== templateId));
  };

  const handleDuplicateTemplate = (template) => {
    const duplicatedTemplate = {
      ...template,
      id: Date.now(),
      name: `${template.name} (Copy)`,
      createdAt: new Date().toISOString()
    };
    setTemplates([...templates, duplicatedTemplate]);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">Task Templates</h3>
          <p className="text-sm text-gray-600">Pre-defined task structures for common workflows</p>
        </div>
        <Button
          onClick={() => setShowCreateForm(true)}
          className="bg-blue-600 hover:bg-blue-700 text-white"
        >
          <Plus className="h-4 w-4 mr-2" />
          Create Template
        </Button>
      </div>

      {/* Template Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {templates.map((template) => (
          <Card key={template.id} className="p-4 hover:shadow-lg transition-shadow">
            <div className="flex items-start justify-between mb-3">
              <div className="flex-1">
                <h4 className="font-semibold text-gray-900 mb-1">{template.name}</h4>
                <p className="text-sm text-gray-600 mb-2">{template.description}</p>
                <div className="flex items-center gap-2">
                  <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                    {template.category}
                  </span>
                  <span className="text-xs text-gray-500">
                    {template.tasks.length} tasks
                  </span>
                </div>
              </div>
              <div className="flex items-center gap-1">
                <button
                  onClick={() => handleDuplicateTemplate(template)}
                  className="p-1 hover:bg-gray-100 rounded"
                  title="Duplicate"
                >
                  <Copy className="h-4 w-4 text-gray-500" />
                </button>
                <button
                  onClick={() => handleDeleteTemplate(template.id)}
                  className="p-1 hover:bg-red-100 rounded text-red-500"
                  title="Delete"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>

            <div className="space-y-2 mb-4">
              {template.tasks.slice(0, 3).map((task, index) => (
                <div key={index} className="flex items-center gap-2 text-sm">
                  <CheckCircle className="h-3 w-3 text-gray-400" />
                  <span className="text-gray-700 truncate">{task.title}</span>
                  <span className={`px-1 py-0.5 text-xs rounded ${
                    task.priority === 'high' ? 'bg-red-100 text-red-800' :
                    task.priority === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-green-100 text-green-800'
                  }`}>
                    {task.priority}
                  </span>
                </div>
              ))}
              {template.tasks.length > 3 && (
                <div className="text-xs text-gray-500">
                  +{template.tasks.length - 3} more tasks
                </div>
              )}
            </div>

            <Button
              onClick={() => handleTemplateSelect(template)}
              className="w-full bg-green-600 hover:bg-green-700 text-white"
            >
              Use Template
            </Button>
          </Card>
        ))}
      </div>

      {/* Create Template Form */}
      {showCreateForm && (
        <CreateTemplateForm
          onSave={handleCreateTemplate}
          onCancel={() => setShowCreateForm(false)}
        />
      )}
    </div>
  );
};

// Create Template Form Component
const CreateTemplateForm = ({ onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: 'Development',
    tasks: [{ title: '', description: '', priority: 'medium' }]
  });

  const handleAddTask = () => {
    setFormData({
      ...formData,
      tasks: [...formData.tasks, { title: '', description: '', priority: 'medium' }]
    });
  };

  const handleTaskChange = (index, field, value) => {
    const newTasks = [...formData.tasks];
    newTasks[index][field] = value;
    setFormData({ ...formData, tasks: newTasks });
  };

  const handleRemoveTask = (index) => {
    const newTasks = formData.tasks.filter((_, i) => i !== index);
    setFormData({ ...formData, tasks: newTasks });
  };

  const handleSave = () => {
    if (formData.name && formData.tasks.length > 0) {
      onSave(formData);
    }
  };

  return (
    <Card className="p-6">
      <h4 className="text-lg font-semibold text-gray-900 mb-4">Create New Template</h4>
      
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Template Name</label>
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Enter template name"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
          <textarea
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            rows="2"
            placeholder="Enter template description"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Category</label>
          <select
            value={formData.category}
            onChange={(e) => setFormData({ ...formData, category: e.target.value })}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          >
            <option value="Development">Development</option>
            <option value="Process">Process</option>
            <option value="DevOps">DevOps</option>
            <option value="Design">Design</option>
            <option value="Testing">Testing</option>
          </select>
        </div>

        <div>
          <div className="flex items-center justify-between mb-2">
            <label className="block text-sm font-medium text-gray-700">Tasks</label>
            <Button
              onClick={handleAddTask}
              className="bg-blue-600 hover:bg-blue-700 text-white text-sm"
            >
              <Plus className="h-4 w-4 mr-1" />
              Add Task
            </Button>
          </div>
          
          <div className="space-y-2">
            {formData.tasks.map((task, index) => (
              <div key={index} className="flex items-center gap-2 p-3 border border-gray-200 rounded-lg">
                <div className="flex-1 grid grid-cols-1 md:grid-cols-3 gap-2">
                  <input
                    type="text"
                    value={task.title}
                    onChange={(e) => handleTaskChange(index, 'title', e.target.value)}
                    placeholder="Task title"
                    className="px-2 py-1 border border-gray-300 rounded text-sm"
                  />
                  <input
                    type="text"
                    value={task.description}
                    onChange={(e) => handleTaskChange(index, 'description', e.target.value)}
                    placeholder="Task description"
                    className="px-2 py-1 border border-gray-300 rounded text-sm"
                  />
                  <select
                    value={task.priority}
                    onChange={(e) => handleTaskChange(index, 'priority', e.target.value)}
                    className="px-2 py-1 border border-gray-300 rounded text-sm"
                  >
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                  </select>
                </div>
                <button
                  onClick={() => handleRemoveTask(index)}
                  className="p-1 hover:bg-red-100 rounded text-red-500"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            ))}
          </div>
        </div>

        <div className="flex items-center gap-3 pt-4">
          <Button
            onClick={handleSave}
            className="bg-green-600 hover:bg-green-700 text-white"
          >
            Save Template
          </Button>
          <Button
            onClick={onCancel}
            variant="outline"
          >
            Cancel
          </Button>
        </div>
      </div>
    </Card>
  );
};

export default TaskTemplateManager;
