import React, { useMemo, useState } from 'react';
import { useQuery } from 'react-query';
import { tasksAPI } from '../../services/api';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import {
  LayoutDashboard,
  CheckSquare,
  Kanban,
  FolderOpen,
  Users,
  User,
  LogOut,
  Menu,
  X,
  Bell,
} from 'lucide-react';

const Layout = ({ children }) => {
  const { user, logout } = useAuth();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [logoutPassword, setLogoutPassword] = useState('');
  const [showNotif, setShowNotif] = useState(false);

  const { data: notifResp } = useQuery(
    'notifications',
    tasksAPI.getNotifications,
    { refetchInterval: 15000 }
  );
  const notificationsRaw = notifResp?.data?.items || notifResp?.items || [];
  const [clearedAt, setClearedAt] = useState(() => Number(window.localStorage.getItem('tf_cleared_at') || 0));
  const notifications = useMemo(() => {
    if (!clearedAt) return notificationsRaw;
    return (notificationsRaw || []).filter((n) => new Date(n.created_at).getTime() > clearedAt);
  }, [notificationsRaw, clearedAt]);

  const navigation = [
    { name: 'Dashboard', href: '/dashboard', icon: LayoutDashboard },
    { name: 'Tasks', href: '/tasks', icon: CheckSquare },
    { name: 'Kanban', href: '/kanban', icon: Kanban },
    { name: 'Projects', href: '/projects', icon: FolderOpen },
    ...(user?.role === 'scrum_master' ? [{ name: 'Users', href: '/users', icon: Users }] : []),
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Mobile sidebar */}
      <div className={`fixed inset-0 z-50 lg:hidden ${sidebarOpen ? 'block' : 'hidden'}`}>
        <div className="fixed inset-0 bg-gray-600 bg-opacity-75" onClick={() => setSidebarOpen(false)} />
        <div className="fixed inset-y-0 left-0 flex w-64 flex-col bg-white shadow-xl">
          <div className="flex h-16 items-center justify-between px-4">
            <h1 className="text-xl font-bold text-gray-900">TaskFlow</h1>
            <button
              onClick={() => setSidebarOpen(false)}
              className="text-gray-400 hover:text-gray-600"
            >
              <X className="h-6 w-6" />
            </button>
          </div>
          <nav className="flex-1 px-4 py-4 space-y-2">
            {navigation.map((item) => {
              const Icon = item.icon;
              return (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                    isActive(item.href)
                      ? 'bg-blue-100 text-blue-700'
                      : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                  }`}
                  onClick={() => setSidebarOpen(false)}
                >
                  <Icon className="mr-3 h-5 w-5" />
                  {item.name}
                </Link>
              );
            })}
          </nav>
        </div>
      </div>

      {/* Desktop sidebar */}
      <div className="hidden lg:fixed lg:inset-y-0 lg:flex lg:w-64 lg:flex-col">
        <div className="flex flex-col flex-grow bg-white border-r border-gray-200">
          <div className="flex h-16 items-center px-4">
            <h1 className="text-xl font-bold text-gray-900">TaskFlow</h1>
          </div>
          <nav className="flex-1 px-4 py-4 space-y-2">
            {navigation.map((item) => {
              const Icon = item.icon;
              return (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                    isActive(item.href)
                      ? 'bg-blue-100 text-blue-700'
                      : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                  }`}
                >
                  <Icon className="mr-3 h-5 w-5" />
                  {item.name}
                </Link>
              );
            })}
          </nav>
        </div>
      </div>

      {/* Main content */}
      <div className="lg:pl-64">
        {/* Top bar */}
        <div className="sticky top-0 z-40 flex h-16 items-center justify-between bg-white border-b border-gray-200 px-4 lg:px-8">
          <button
            onClick={() => setSidebarOpen(true)}
            className="text-gray-500 hover:text-gray-600 lg:hidden"
          >
            <Menu className="h-6 w-6" />
          </button>
          
          <div className="flex items-center space-x-4 ml-auto">
            <div className="relative">
              <button className="relative text-gray-400 hover:text-gray-600" onClick={() => setShowNotif(!showNotif)}>
                <Bell className="h-6 w-6" />
                {notifications.length > 0 && (
                  <span className="absolute -top-1 -right-1 inline-flex items-center justify-center px-1.5 py-0.5 text-[10px] font-medium leading-none text-white bg-red-600 rounded-full">
                    {Math.min(notifications.length, 9)}+
                  </span>
                )}
              </button>
              {showNotif && (
                <div className="absolute right-0 mt-2 w-96 max-h-96 overflow-y-auto bg-white border border-gray-200 rounded-lg shadow-xl z-50">
                  <div className="p-3 border-b flex items-center justify-between">
                    <div className="font-semibold text-gray-900">Notifications</div>
                    <button
                      className="text-xs text-red-600 hover:text-red-700"
                      onClick={() => {
                        const now = Date.now();
                        window.localStorage.setItem('tf_cleared_at', String(now));
                        setClearedAt(now);
                      }}
                    >
                      Clear all
                    </button>
                  </div>
                  <div className="p-2 space-y-2">
                    {notifications.length === 0 ? (
                      <div className="p-4 text-sm text-gray-500">No notifications</div>
                    ) : (
                      notifications.slice(0, 20).map((n) => (
                        <div key={`${n.type}-${n.id}`} className="p-3 bg-gray-50 rounded border border-gray-100">
                          <div className="flex items-center justify-between text-xs text-gray-500">
                            <span className={`px-1.5 py-0.5 rounded ${n.type === 'activity' ? 'bg-blue-100 text-blue-700' : (n.type === 'due_soon' ? 'bg-yellow-100 text-yellow-700' : 'bg-green-100 text-green-700')}`}>{n.type === 'activity' ? 'Activity' : (n.type === 'due_soon' ? 'Due Soon' : 'Message')}</span>
                            <span>{new Date(n.created_at).toLocaleString()}</span>
                          </div>
                          <div className="mt-1 text-sm text-gray-900">
                            {n.type === 'activity' ? (
                              <>
                                <p className="font-medium line-clamp-2">{n.description}</p>
                                {n.task_title && (<p className="text-xs text-gray-500 mt-1">Task: {n.task_title}</p>)}
                              </>
                            ) : n.type === 'due_soon' ? (
                              <>
                                <p className="font-medium line-clamp-2">{n.content}</p>
                                {n.project_name && (<p className="text-xs text-gray-500 mt-1">Project: {n.project_name}</p>)}
                              </>
                            ) : (
                              <>
                                <p className="font-medium line-clamp-2">{n.content}</p>
                                <p className="text-xs text-gray-500 mt-1">From: {n.author_name}{n.project_name ? ` • ${n.project_name}` : ''}</p>
                              </>
                            )}
                          </div>
                        </div>
                      ))
                    )}
                  </div>
                </div>
              )}
            </div>
            
            <div className="flex items-center space-x-3">
              <div className="text-right">
                <p className="text-sm font-medium text-gray-900">{user?.first_name ? `${user.first_name} ${user.last_name || ''}`.trim() : (user?.username || '')}</p>
                <p className="text-xs text-gray-500 capitalize">{user?.role?.replace('_', ' ')}</p>
              </div>
              
              <div className="flex items-center space-x-2">
                <Link
                  to="/profile"
                  className="text-gray-400 hover:text-gray-600"
                >
                  <User className="h-6 w-6" />
                </Link>
                <button
                  onClick={() => setShowLogoutModal(true)}
                  className="text-gray-400 hover:text-gray-600"
                  title="Logout"
                >
                  <LogOut className="h-6 w-6" />
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Page content */}
        <main className="p-4 lg:p-8">
          {children}
        </main>

        {/* Logout Confirmation Modal (in-page) */}
        {showLogoutModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center">
            <div className="absolute inset-0 bg-gray-900/40" onClick={() => setShowLogoutModal(false)} />
            <div className="relative bg-white rounded-lg shadow-xl w-full max-w-md p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Confirm Logout</h3>
              <p className="text-sm text-gray-600 mb-4">For security, please enter your password to logout.</p>
              <input
                type="password"
                className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter your password"
                value={logoutPassword}
                onChange={(e) => setLogoutPassword(e.target.value)}
              />
              <div className="mt-4 flex justify-end gap-2">
                <button
                  className="px-4 py-2 text-sm rounded-md border border-gray-300 text-gray-700 hover:bg-gray-50"
                  onClick={() => {
                    setShowLogoutModal(false);
                    setLogoutPassword('');
                  }}
                >
                  Cancel
                </button>
                <button
                  className={`px-4 py-2 text-sm rounded-md text-white ${logoutPassword ? 'bg-red-600 hover:bg-red-700' : 'bg-red-300 cursor-not-allowed'}`}
                  disabled={!logoutPassword}
                  onClick={() => {
                    // Optionally verify password with backend before logout
                    logout();
                    setShowLogoutModal(false);
                    setLogoutPassword('');
                  }}
                >
                  Logout
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Layout;
