import React, { useState, useEffect, useRef } from 'react';
import { useMutation, useQuery, useQueryClient } from 'react-query';
import { useAuth } from '../../contexts/AuthContext';
import { projectsAPI } from '../../services/api';
import Button from '../UI/Button';
import Input from '../UI/Input';
import { Send, MessageSquare, Users, Clock, Paperclip, Mic } from 'lucide-react';
import toast from 'react-hot-toast';

const ProjectChat = ({ projectId, projectName }) => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [attachment, setAttachment] = useState(null);
  const [recording, setRecording] = useState(false);
  const mediaRecorderRef = useRef(null);
  const messagesEndRef = useRef(null);
  const typingTimeoutRef = useRef(null);
  const [timeTick, setTimeTick] = useState(Date.now());

  // Fetch project messages with real-time updates
  const { data: messagesResp, isLoading } = useQuery(
    ['project-messages', projectId],
    () => projectsAPI.getProjectMessages(projectId),
    {
      enabled: !!projectId,
      refetchInterval: 2000, // Refresh every 2 seconds for real-time feel
      onError: (error) => {
        console.error('Error fetching messages:', error);
      }
    }
  );

  // Send message mutation
  const sendMessageMutation = useMutation(
    (messageData) => projectsAPI.createProjectMessage(projectId, messageData),
    {
      onSuccess: () => {
        setNewMessage('');
        queryClient.invalidateQueries(['project-messages', projectId]);
        toast.success('Message sent!');
      },
      onError: (error) => {
        console.error('Send message error:', error);
        
        // Show specific error message
        if (error.response?.data?.content) {
          toast.error(error.response.data.content[0] || 'Invalid message content');
        } else if (error.response?.status === 403) {
          toast.error('You must be a project member to send messages');
        } else if (error.response?.status === 400) {
          toast.error('Invalid message. Please check your input.');
        } else {
          toast.error('Failed to send message. Please try again.');
        }
      },
    }
  );

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messagesResp]);

  // Re-render periodically so relative times stay accurate
  useEffect(() => {
    const interval = setInterval(() => setTimeTick(Date.now()), 30000); // every 30s
    return () => clearInterval(interval);
  }, []);

  // Normalize response
  const messages = Array.isArray(messagesResp)
    ? messagesResp
    : (Array.isArray(messagesResp?.data)
        ? messagesResp.data
        : (messagesResp?.data?.results || []));

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() && !attachment) return;

    const messageData = {
      content: newMessage.trim() || (attachment ? `[${attachment.type.includes('audio') ? 'Voice message' : 'File'}]` : ''),
    };
    
    // For now, just send the message without attachment until backend supports it
    // TODO: Implement file upload when backend is ready
    if (attachment) {
      console.log('Attachment selected:', attachment.name, attachment.type);
      // For voice messages, we could convert to text or just send a placeholder
      if (attachment.type.includes('audio')) {
        messageData.content = `ðŸŽ¤ Voice message: ${attachment.name}`;
      } else {
        messageData.content = `ðŸ“Ž File: ${attachment.name}`;
      }
    }
    
    console.log('Sending message:', messageData);
    sendMessageMutation.mutate(messageData);
    setAttachment(null);
  };

  const handleTyping = (e) => {
    setNewMessage(e.target.value);
    
    // Clear existing timeout
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }
    
    // Set typing indicator
    setIsTyping(true);
    
    // Clear typing indicator after 1 second of no typing
    typingTimeoutRef.current = setTimeout(() => {
      setIsTyping(false);
    }, 1000);
  };

  const formatMessageTime = (timestamp) => {
    // Ensure we use the actual message time; timeTick is referenced so this recomputes periodically
    void timeTick; // referenced to trigger re-render
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now - date;
    const diffSec = Math.floor(diffMs / 1000);
    const diffMin = Math.floor(diffSec / 60);
    const diffHr = Math.floor(diffMin / 60);
    const diffDay = Math.floor(diffHr / 24);

    if (diffSec < 10) return 'Just now';
    if (diffSec < 60) return `${diffSec}s ago`;
    if (diffMin < 60) return `${diffMin}m ago`;
    if (diffHr < 24) return `${diffHr}h ago`;
    if (diffDay === 1) return `Yesterday ${date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;
    return date.toLocaleDateString([], { month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' });
  };

  const isMyMessage = (message) => {
    return message.author === user?.id;
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full bg-white rounded-lg border border-gray-200">
      {/* Chat Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-gray-50">
        <div className="flex items-center gap-3">
          <MessageSquare className="h-5 w-5 text-blue-600" />
          <div>
            <h3 className="font-semibold text-gray-900">Project Chat</h3>
            <p className="text-sm text-gray-500">{projectName}</p>
          </div>
        </div>
        <div className="flex items-center gap-2 text-sm text-gray-500">
          <Users className="h-4 w-4" />
          <span>{messages?.length || 0} messages</span>
        </div>
      </div>

      {/* Messages Area */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 min-h-[300px] max-h-[400px]">
        {messages && messages.length > 0 ? (
          messages.map((message) => (
            <div
              key={message.id}
              className={`flex ${isMyMessage(message) ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`max-w-[70%] rounded-lg px-4 py-2 ${
                  isMyMessage(message)
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 text-gray-900'
                }`}
              >
                {!isMyMessage(message) && (
                  <div className="text-xs font-medium mb-1 opacity-75">
                    {message.author_name || 'Unknown User'}
                  </div>
                )}
                <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                <div className={`text-xs mt-1 flex items-center gap-1 ${
                  isMyMessage(message) ? 'text-blue-100' : 'text-gray-500'
                }`}>
                  <Clock className="h-3 w-3" />
                  {formatMessageTime(message.created_at)}
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center py-8 text-gray-500">
            <MessageSquare className="h-12 w-12 mx-auto mb-4 text-gray-300" />
            <p className="text-sm">No messages yet</p>
            <p className="text-xs text-gray-400">Start the conversation!</p>
          </div>
        )}
        
        {/* Typing Indicator */}
        {isTyping && (
          <div className="flex justify-start">
            <div className="bg-gray-100 rounded-lg px-4 py-2">
              <div className="flex items-center gap-1">
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                </div>
                <span className="text-xs text-gray-500 ml-2">Someone is typing...</span>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* Message Input */}
      <div className="p-4 border-t border-gray-200 bg-gray-50">
        <form onSubmit={handleSendMessage} className="flex gap-2 items-center">
          <label className="inline-flex items-center justify-center w-10 h-10 rounded-md border border-gray-300 bg-white cursor-pointer hover:bg-gray-100">
            <Paperclip className="h-4 w-4 text-gray-600" />
            <input
              type="file"
              accept="image/*,video/*,.pdf"
              className="hidden"
              onChange={(e) => {
                const file = e.target.files?.[0];
                if (file) {
                  setAttachment(file);
                }
              }}
            />
          </label>
          <button
            type="button"
            className={`inline-flex items-center justify-center w-10 h-10 rounded-md border ${recording ? 'border-red-400 bg-red-50' : 'border-gray-300 bg-white'} hover:bg-gray-100`}
            onClick={async () => {
              try {
                if (!recording) {
                  const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
                  const recorder = new MediaRecorder(stream);
                  const chunks = [];
                  recorder.ondataavailable = (e) => chunks.push(e.data);
                  recorder.onstop = () => {
                    const blob = new Blob(chunks, { type: 'audio/webm' });
                    const file = new File([blob], 'voice-message.webm', { type: 'audio/webm' });
                    setAttachment(file);
                    stream.getTracks().forEach(t => t.stop());
                  };
                  mediaRecorderRef.current = recorder;
                  recorder.start();
                  setRecording(true);
                } else {
                  mediaRecorderRef.current?.stop();
                  setRecording(false);
                }
              } catch (err) {
                toast.error('Microphone access denied');
              }
            }}
            title={recording ? 'Stop Recording' : 'Record Voice Message'}
          >
            <Mic className={`h-4 w-4 ${recording ? 'text-red-500' : 'text-gray-600'}`} />
          </button>
          <Input
            value={newMessage}
            onChange={handleTyping}
            placeholder="Type your message..."
            className="flex-1"
            disabled={sendMessageMutation.isLoading}
          />
          <Button
            type="submit"
            disabled={!newMessage.trim() || sendMessageMutation.isLoading}
            loading={sendMessageMutation.isLoading}
            className="px-4"
          >
            <Send className="h-4 w-4" />
          </Button>
          {attachment && (
            <div className="text-xs text-gray-600 ml-2 truncate max-w-[160px]" title={attachment.name}>
              Attached: {attachment.name}
            </div>
          )}
        </form>
      </div>
    </div>
  );
};

export default ProjectChat;
