import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { projectsAPI, authAPI } from '../../services/api';
import Card from '../UI/Card';
import Button from '../UI/Button';
import Input from '../UI/Input';
import Select from '../UI/Select';
import Badge from '../UI/Badge';
import LoadingSpinner from '../UI/LoadingSpinner';
import { Users, Plus, X, User } from 'lucide-react';
import toast from 'react-hot-toast';

const ProjectMembersModal = ({ project, isOpen, onClose }) => {
  const queryClient = useQueryClient();
  const [showAddForm, setShowAddForm] = useState(false);
  const [selectedUser, setSelectedUser] = useState('');
  const [selectedRole, setSelectedRole] = useState('employee');

  // Fetch project members
  const { data: members, isLoading: membersLoading } = useQuery(
    ['projectMembers', project?.id],
    () => projectsAPI.getProjectMembers(project.id),
    {
      enabled: !!project && isOpen,
    }
  );

  // Fetch all employees for adding to project
  const { data: employees } = useQuery(
    'employees',
    authAPI.getEmployees,
    {
      enabled: showAddForm,
    }
  );

  // Add member mutation
  const addMemberMutation = useMutation(
    (memberData) => projectsAPI.addProjectMember(project.id, memberData),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['projectMembers', project.id]);
        setSelectedUser('');
        setSelectedRole('employee');
        setShowAddForm(false);
        toast.success('Member added successfully');
      },
      onError: (error) => {
        toast.error(error.response?.data?.detail || 'Failed to add member');
      },
    }
  );

  // Remove member mutation
  const removeMemberMutation = useMutation(
    (memberId) => projectsAPI.removeProjectMember(project.id, memberId),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['projectMembers', project.id]);
        toast.success('Member removed successfully');
      },
      onError: (error) => {
        toast.error(error.response?.data?.detail || 'Failed to remove member');
      },
    }
  );

  const handleAddMember = (e) => {
    e.preventDefault();
    if (!selectedUser) {
      toast.error('Please select a user');
      return;
    }

    addMemberMutation.mutate({
      user: selectedUser,
      role: selectedRole,
    });
  };

  const handleRemoveMember = (memberId) => {
    if (window.confirm('Are you sure you want to remove this member from the project?')) {
      removeMemberMutation.mutate(memberId);
    }
  };

  if (!isOpen || !project) return null;

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" onClick={onClose}></div>

        <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-2xl sm:w-full">
          <div className="bg-white">
            {/* Header */}
            <div className="px-6 py-4 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900">
                    Project Members - {project.name}
                  </h3>
                  <p className="text-sm text-gray-500">
                    Manage team members for this project
                  </p>
                </div>
                <button
                  onClick={onClose}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>
            </div>

            {/* Content */}
            <div className="px-6 py-4">
              {/* Add Member Button */}
              <div className="mb-4">
                <Button
                  onClick={() => setShowAddForm(!showAddForm)}
                  className="flex items-center gap-2"
                >
                  <Plus className="h-4 w-4" />
                  Add Member
                </Button>
              </div>

              {/* Add Member Form */}
              {showAddForm && (
                <Card className="p-4 mb-4">
                  <form onSubmit={handleAddMember} className="space-y-4">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <Select
                        label="Select Employee"
                        value={selectedUser}
                        onChange={(e) => setSelectedUser(e.target.value)}
                        required
                      >
                        <option value="">Choose an employee...</option>
                        {employees?.map((employee) => (
                          <option key={employee.id} value={employee.id}>
                            {employee.first_name} {employee.last_name} ({employee.email})
                          </option>
                        ))}
                      </Select>

                      <Select
                        label="Role"
                        value={selectedRole}
                        onChange={(e) => setSelectedRole(e.target.value)}
                        required
                      >
                        <option value="employee">Employee</option>
                        <option value="scrum_master">Scrum Master</option>
                      </Select>
                    </div>

                    <div className="flex items-center gap-2">
                      <Button
                        type="submit"
                        loading={addMemberMutation.isLoading}
                        disabled={addMemberMutation.isLoading}
                        size="sm"
                      >
                        Add Member
                      </Button>
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => setShowAddForm(false)}
                        size="sm"
                      >
                        Cancel
                      </Button>
                    </div>
                  </form>
                </Card>
              )}

              {/* Members List */}
              <div className="space-y-3">
                {membersLoading ? (
                  <div className="flex items-center justify-center py-8">
                    <LoadingSpinner size="md" />
                  </div>
                ) : members && members.length > 0 ? (
                  members.map((member) => (
                    <div key={member.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center space-x-3">
                        <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                          <User className="h-5 w-5 text-blue-600" />
                        </div>
                        <div>
                          <p className="font-medium text-gray-900">
                            {member.user_name}
                          </p>
                          <p className="text-sm text-gray-500">
                            {member.user_email}
                          </p>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-2">
                        <Badge 
                          variant={member.role === 'scrum_master' ? 'primary' : 'gray'}
                          size="sm"
                        >
                          {member.role === 'scrum_master' ? 'Scrum Master' : 'Employee'}
                        </Badge>
                        
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleRemoveMember(member.id)}
                          className="text-red-600 hover:text-red-800"
                        >
                          <X className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-8">
                    <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500">No members assigned yet</p>
                    <p className="text-sm text-gray-400">Add members to get started</p>
                  </div>
                )}
              </div>
            </div>

            {/* Footer */}
            <div className="px-6 py-4 border-t border-gray-200">
              <Button
                onClick={onClose}
                className="w-full"
              >
                Close
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectMembersModal;
