import React from 'react';
import { useQuery } from 'react-query';
import { projectsAPI, tasksAPI } from '../../services/api';
import Card from '../UI/Card';
import { Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);
import { 
  BarChart3, 
  CheckCircle, 
  Clock, 
  Users, 
  TrendingUp, 
  Calendar,
  Target,
  Zap,
  Award,
  Activity,
  PieChart,
  ArrowUp,
  ArrowDown,
  Minus,
  Star,
  Timer,
  AlertCircle,
  LineChart,
  TrendingDown
} from 'lucide-react';

const ProjectAnalytics = ({ projectId, projectName }) => {
  console.log('ProjectAnalytics rendered with:', { projectId, projectName });
  console.log('ProjectAnalytics: About to fetch analytics for project:', projectId);

  // Fetch project-specific analytics
  const { data: analyticsResp, isLoading, error } = useQuery(
    ['project-analytics', projectId],
    async () => {
      try {
        console.log('Fetching analytics for project:', projectId);
        // Try project-specific analytics first
        const res = await projectsAPI.getProjectAnalytics(projectId);
        console.log('Project analytics response:', res);
        return res.data || res;
      } catch (error) {
        console.error('Project-specific analytics failed, falling back to general analytics:', error);
        // Fallback to general task analytics if project-specific fails
        const res = await tasksAPI.getTaskAnalytics();
        console.log('Fallback analytics response:', res);
        return res.data || res;
      }
    },
    {
      enabled: !!projectId,
      refetchInterval: 30000, // Refresh every 30 seconds
    }
  );
  const analytics = analyticsResp?.data || analyticsResp;

  console.log('Analytics data:', analytics);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (!analytics) {
    console.log('No analytics data, showing fallback UI');
    return (
      <div className="text-center py-8">
        <BarChart3 className="h-12 w-12 text-gray-400 mx-auto mb-4" />
        <p className="text-gray-500">No analytics data available</p>
        <p className="text-sm text-gray-400 mt-2">Project ID: {projectId}</p>
      </div>
    );
  }

  // Handle both project-specific analytics and general analytics
  const isProjectSpecific = analytics && analytics.project;
  const displayProjectName = isProjectSpecific ? analytics.project.name : projectName;

  // Calculate completion rate first
  const completionRate = analytics.total_tasks > 0 
    ? Math.round((analytics.completed_tasks / analytics.total_tasks) * 100) 
    : 0;

  const stats = [
    {
      title: 'Total Tasks',
      value: analytics.total_tasks || 0,
      icon: BarChart3,
      color: 'text-blue-600',
      bgColor: 'bg-gradient-to-br from-blue-100 to-blue-200',
    },
    {
      title: 'Completed Tasks',
      value: analytics.completed_tasks || 0,
      icon: CheckCircle,
      color: 'text-green-600',
      bgColor: 'bg-gradient-to-br from-green-100 to-green-200',
    },
    {
      title: 'In Progress',
      value: analytics.in_progress_tasks || 0,
      icon: Clock,
      color: 'text-yellow-600',
      bgColor: 'bg-gradient-to-br from-yellow-100 to-yellow-200',
    },
    {
      title: 'Team Members',
      value: analytics.team_members || 0,
      icon: Users,
      color: 'text-purple-600',
      bgColor: 'bg-gradient-to-br from-purple-100 to-purple-200',
    },
  ];

  const additionalStats = [
    {
      title: 'Avg. Task Duration',
      value: analytics.avg_task_duration ? `${analytics.avg_task_duration} days` : 'N/A',
      icon: Timer,
      color: 'text-indigo-600',
      bgColor: 'bg-gradient-to-br from-indigo-100 to-indigo-200',
    },
    {
      title: 'Completion Rate',
      value: `${completionRate}%`,
      icon: Target,
      color: 'text-green-600',
      bgColor: 'bg-gradient-to-br from-green-100 to-green-200',
    },
    {
      title: 'Quality Rating',
      value: analytics.quality_rating ? `${analytics.quality_rating}/5` : 'N/A',
      icon: Star,
      color: 'text-pink-600',
      bgColor: 'bg-gradient-to-br from-pink-100 to-pink-200',
    },
    {
      title: 'Risk Level',
      value: completionRate < 30 ? 'High' : completionRate < 70 ? 'Medium' : 'Low',
      icon: AlertCircle,
      color: completionRate < 30 ? 'text-red-600' : completionRate < 70 ? 'text-yellow-600' : 'text-green-600',
      bgColor: completionRate < 30 ? 'bg-gradient-to-br from-red-100 to-red-200' : completionRate < 70 ? 'bg-gradient-to-br from-yellow-100 to-yellow-200' : 'bg-gradient-to-br from-green-100 to-green-200',
    },
  ];

  // Chart data - only from real analytics
  const taskStatusData = [
    { label: 'Completed', value: analytics.completed_tasks || 0, color: '#10B981' },
    { label: 'In Progress', value: analytics.in_progress_tasks || 0, color: '#F59E0B' },
    { label: 'To Do', value: (analytics.total_tasks || 0) - (analytics.completed_tasks || 0) - (analytics.in_progress_tasks || 0), color: '#6B7280' },
  ];

  // Only show charts if we have meaningful data
  const hasTaskData = (analytics.total_tasks || 0) > 0;
  const hasTeamData = analytics.team_performance && analytics.team_performance.length > 0;

  // Create sample data for demonstration if no real data
  const sampleTaskData = [
    { label: 'Completed', value: 8, color: '#10B981' },
    { label: 'In Progress', value: 3, color: '#F59E0B' },
    { label: 'To Do', value: 4, color: '#6B7280' },
  ];

  const displayTaskData = hasTaskData ? taskStatusData : sampleTaskData;

  // Chart data for tasks by status (same as dashboard)
  const statusChartData = {
    labels: ['To Do', 'In Progress', 'Done'],
    datasets: [
      {
        data: [
          analytics.todo_tasks || 0,
          analytics.in_progress_tasks || 0,
          analytics.completed_tasks || 0,
        ],
        backgroundColor: ['#6b7280', '#f59e0b', '#10b981'],
        borderWidth: 0,
      },
    ],
  };

  const statusChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
      },
    },
  };

  // Chart data for tasks by priority (same as dashboard)
  const priorityItems = Array.isArray(analytics?.tasks_by_priority) ? analytics.tasks_by_priority : [];
  const priorityChartData = {
    labels: priorityItems.map(item => (item.priority || '').toString().charAt(0).toUpperCase() + (item.priority || '').toString().slice(1)),
    datasets: [
      {
        label: 'Tasks',
        data: priorityItems.map(item => item.count || 0),
        backgroundColor: ['#ef4444', '#f59e0b', '#3b82f6', '#10b981'],
        borderWidth: 0,
      },
    ],
  };

  const priorityChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 1,
        },
      },
    },
  };

  // Pie Chart Component
  const PieChartComponent = ({ data, size = 200 }) => {
    const total = data.reduce((sum, item) => sum + item.value, 0);
    let cumulativePercentage = 0;

    return (
      <div className="relative" style={{ width: size, height: size }}>
        <svg width={size} height={size} className="transform -rotate-90">
          {data.map((item, index) => {
            const percentage = total > 0 ? (item.value / total) * 100 : 0;
            const circumference = 2 * Math.PI * 45; // radius = 45
            const strokeDasharray = `${(percentage / 100) * circumference} ${circumference}`;
            const strokeDashoffset = -((cumulativePercentage / 100) * circumference);

            cumulativePercentage += percentage;

            return (
              <circle
                key={index}
                cx={size / 2}
                cy={size / 2}
                r="45"
                fill="none"
                stroke={item.color}
                strokeWidth="20"
                strokeDasharray={strokeDasharray}
                strokeDashoffset={strokeDashoffset}
                className="transition-all duration-1000 ease-out"
                style={{ transitionDelay: `${index * 200}ms` }}
              />
            );
          })}
        </svg>
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="text-center">
            <div className="text-2xl font-bold text-gray-900">{total}</div>
            <div className="text-sm text-gray-500">Total Tasks</div>
          </div>
        </div>
      </div>
    );
  };

  // Bar Chart Component for Task Status
  const BarChartComponent = ({ data, title }) => {
    const maxValue = Math.max(...data.map(item => item.value));

    return (
      <div className="space-y-4">
        <h4 className="text-lg font-semibold text-gray-900">{title}</h4>
        <div className="space-y-4">
          {data.map((item, index) => (
            <div key={index} className="space-y-2">
              <div className="flex justify-between text-sm">
                <span className="font-medium text-gray-700">{item.label}</span>
                <span className="text-gray-500">{item.value} tasks</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-4">
                <div
                  className="h-4 rounded-full transition-all duration-1000 ease-out"
                  style={{ 
                    width: `${maxValue > 0 ? (item.value / maxValue) * 100 : 0}%`,
                    backgroundColor: item.color,
                    transitionDelay: `${index * 200}ms`
                  }}
                ></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  // Donut Chart Component (alternative to pie chart)
  const DonutChartComponent = ({ data, size = 200 }) => {
    const total = data.reduce((sum, item) => sum + item.value, 0);
    let cumulativePercentage = 0;

    return (
      <div className="relative" style={{ width: size, height: size }}>
        <svg width={size} height={size} className="transform -rotate-90">
          {/* Background circle */}
          <circle
            cx={size / 2}
            cy={size / 2}
            r="60"
            fill="none"
            stroke="#E5E7EB"
            strokeWidth="12"
          />
          {/* Data segments */}
          {data.map((item, index) => {
            const percentage = total > 0 ? (item.value / total) * 100 : 0;
            const circumference = 2 * Math.PI * 60; // radius = 60
            const strokeDasharray = `${(percentage / 100) * circumference} ${circumference}`;
            const strokeDashoffset = -((cumulativePercentage / 100) * circumference);

            cumulativePercentage += percentage;

            return (
              <circle
                key={index}
                cx={size / 2}
                cy={size / 2}
                r="60"
                fill="none"
                stroke={item.color}
                strokeWidth="12"
                strokeDasharray={strokeDasharray}
                strokeDashoffset={strokeDashoffset}
                className="transition-all duration-1000 ease-out"
                style={{ transitionDelay: `${index * 200}ms` }}
                strokeLinecap="round"
              />
            );
          })}
        </svg>
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="text-center">
            <div className="text-3xl font-bold text-gray-900">{total}</div>
            <div className="text-sm text-gray-500">Total Tasks</div>
          </div>
        </div>
      </div>
    );
