import React from 'react';
import { useQuery } from 'react-query';
import { projectsAPI, tasksAPI } from '../../services/api';
import Card from '../UI/Card';
import { BarChart3, CheckCircle, Clock, Users, TrendingUp, Calendar } from 'lucide-react';

const ProjectAnalytics = ({ projectId, projectName }) => {
  // Fetch project-specific analytics
  const { data: analyticsResp, isLoading, error } = useQuery(
    ['project-analytics', projectId],
    async () => {
      try {
        // Try project-specific analytics first
        const res = await projectsAPI.getProjectAnalytics(projectId);
        return res.data || res;
      } catch (error) {
        console.error('Project-specific analytics failed, falling back to general analytics:', error);
        // Fallback to general task analytics if project-specific fails
        const res = await tasksAPI.getTaskAnalytics();
        return res.data || res;
      }
    },
    {
      enabled: !!projectId,
      refetchInterval: 30000, // Refresh every 30 seconds
    }
  );
  const analytics = analyticsResp?.data || analyticsResp;

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (!analytics) {
    return (
      <div className="text-center py-8">
        <BarChart3 className="h-12 w-12 text-gray-400 mx-auto mb-4" />
        <p className="text-gray-500">No analytics data available</p>
      </div>
    );
  }

  // Handle both project-specific analytics and general analytics
  const isProjectSpecific = analytics && analytics.project;
  const displayProjectName = isProjectSpecific ? analytics.project.name : projectName;
  
  const stats = [
    {
      title: 'Total Tasks',
      value: analytics.total_tasks || 0,
      icon: BarChart3,
      color: 'text-blue-600',
      bgColor: 'bg-blue-100',
    },
    {
      title: 'Completed Tasks',
      value: analytics.completed_tasks || 0,
      icon: CheckCircle,
      color: 'text-green-600',
      bgColor: 'bg-green-100',
    },
    {
      title: 'In Progress',
      value: analytics.in_progress_tasks || 0,
      icon: Clock,
      color: 'text-yellow-600',
      bgColor: 'bg-yellow-100',
    },
    {
      title: 'Team Members',
      value: analytics.team_members || 0,
      icon: Users,
      color: 'text-purple-600',
      bgColor: 'bg-purple-100',
    },
  ];

  const completionRate = analytics.total_tasks > 0 
    ? Math.round((analytics.completed_tasks / analytics.total_tasks) * 100) 
    : 0;

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl font-semibold text-gray-900">Project Analytics</h2>
          {displayProjectName && (
            <p className="text-sm text-gray-600 mt-1">{displayProjectName} - Detailed insights and performance metrics</p>
          )}
        </div>
        <div className="flex items-center gap-2 text-sm text-gray-500">
          <Calendar className="h-4 w-4" />
          <span>Last updated: {new Date().toLocaleTimeString()}</span>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {stats.map((stat, index) => (
          <Card key={index} className="p-6">
            <div className="flex items-center">
              <div className={`p-3 rounded-lg ${stat.bgColor}`}>
                <stat.icon className={`h-6 w-6 ${stat.color}`} />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">{stat.title}</p>
                <p className="text-2xl font-semibold text-gray-900">{stat.value}</p>
              </div>
            </div>
          </Card>
        ))}
      </div>

      {/* Progress Overview */}
      <Card className="p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Project Progress</h3>
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <span className="text-sm font-medium text-gray-700">Completion Rate</span>
            <span className="text-sm font-semibold text-gray-900">{completionRate}%</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-3">
            <div
              className="bg-gradient-to-r from-blue-500 to-green-500 h-3 rounded-full transition-all duration-500"
              style={{ width: `${completionRate}%` }}
            ></div>
          </div>
          <div className="flex items-center gap-4 text-sm text-gray-600">
            <div className="flex items-center gap-1">
              <div className="w-3 h-3 bg-green-500 rounded-full"></div>
              <span>{analytics.completed_tasks || 0} Completed</span>
            </div>
            <div className="flex items-center gap-1">
              <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
              <span>{analytics.in_progress_tasks || 0} In Progress</span>
            </div>
            <div className="flex items-center gap-1">
              <div className="w-3 h-3 bg-gray-400 rounded-full"></div>
              <span>{(analytics.total_tasks || 0) - (analytics.completed_tasks || 0) - (analytics.in_progress_tasks || 0)} To Do</span>
            </div>
          </div>
        </div>
      </Card>

      {/* Task History */}
      {analytics.recent_completed_tasks && analytics.recent_completed_tasks.length > 0 && (
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Recently Completed Tasks</h3>
          <div className="space-y-3">
            {analytics.recent_completed_tasks.slice(0, 5).map((task, index) => (
              <div key={index} className="flex items-center justify-between p-3 bg-green-50 rounded-lg">
                <div className="flex items-center gap-3">
                  <CheckCircle className="h-5 w-5 text-green-600" />
                  <div>
                    <p className="text-sm font-medium text-gray-900">{task.title}</p>
                    <p className="text-xs text-gray-500">
                      Completed by {task.completed_by} â€¢ {new Date(task.completed_at).toLocaleDateString()}
                    </p>
                  </div>
                </div>
                <div className="text-xs text-gray-500">
                  {task.priority && (
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      task.priority === 'high' ? 'bg-red-100 text-red-800' :
                      task.priority === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-green-100 text-green-800'
                    }`}>
                      {task.priority}
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}

      {/* Team Performance */}
      {analytics.team_performance && analytics.team_performance.length > 0 && (
        <Card className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Team Performance</h3>
          <div className="space-y-3">
            {analytics.team_performance.map((member, index) => (
              <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center gap-3">
                  <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                    <Users className="h-4 w-4 text-blue-600" />
                  </div>
                  <div>
                    <p className="text-sm font-medium text-gray-900">{member.name}</p>
                    <p className="text-xs text-gray-500">{member.email}</p>
                  </div>
                </div>
                <div className="text-right">
                  <p className="text-sm font-semibold text-gray-900">{member.completed_tasks || 0}</p>
                  <p className="text-xs text-gray-500">tasks completed</p>
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}

      {/* Project Timeline */}
      <Card className="p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Project Timeline</h3>
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <span className="text-sm text-gray-600">Project Started</span>
            <span className="text-sm font-medium text-gray-900">
              {analytics.project_start_date ? new Date(analytics.project_start_date).toLocaleDateString() : 'N/A'}
            </span>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-sm text-gray-600">Last Activity</span>
            <span className="text-sm font-medium text-gray-900">
              {analytics.last_activity ? new Date(analytics.last_activity).toLocaleDateString() : 'N/A'}
            </span>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-sm text-gray-600">Average Task Duration</span>
            <span className="text-sm font-medium text-gray-900">
              {analytics.avg_task_duration ? `${analytics.avg_task_duration} days` : 'N/A'}
            </span>
          </div>
        </div>
      </Card>
    </div>
  );
};

export default ProjectAnalytics;
