import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
  Filler,
} from 'chart.js';
import { Bar, Doughnut, Line, Pie } from 'react-chartjs-2';
import Card from '../UI/Card';
import Badge from '../UI/Badge';
import { 
  TrendingUp, 
  Users, 
  Clock, 
  CheckCircle, 
  AlertCircle, 
  XCircle, 
  Target,
  Zap,
  Calendar,
  Award,
  Activity,
  BarChart3,
  PieChart,
  LineChart
} from 'lucide-react';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
  Filler
);

const ProjectAnalytics = ({ analytics, projectName, isLoading = false }) => {
  // Helper function to format duration
  const formatDuration = (days, minutes) => {
    if (!days && !minutes) return 'N/A';
    
    const daysInt = Math.floor(days);
    const hours = Math.floor((days - daysInt) * 24);
    const mins = Math.floor(minutes % 60);
    
    if (daysInt > 0) {
