                  return (
                    <div key={index} className="flex items-center justify-between p-4 bg-white rounded-xl border border-emerald-100 hover:shadow-md transition-all duration-200">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gradient-to-br from-emerald-400 to-green-500 rounded-full flex items-center justify-center shadow-lg">
                          <span className="text-white font-bold text-lg">
                            {(member.assignee_name || 'U').charAt(0).toUpperCase()}
                          </span>
                        </div>
                        <div>
                          <p className="font-bold text-gray-900 text-lg">{member.assignee_name || 'Unknown User'}</p>
                          <p className="text-sm text-gray-500">Team Member</p>
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="flex items-center gap-4 mb-2">
                          <div className="text-center">
                            <div className="text-xl font-bold text-green-600">{member.completed_count || 0}</div>
                            <div className="text-xs text-gray-500">Completed</div>
                          </div>
                          <div className="text-center">
                            <div className="text-xl font-bold text-yellow-600">{member.in_progress_count || 0}</div>
                            <div className="text-xs text-gray-500">In Progress</div>
                          </div>
                          <div className="text-center">
                            <div className="text-xl font-bold text-gray-600">{member.todo_count || 0}</div>
                            <div className="text-xs text-gray-500">To Do</div>
                          </div>
                        </div>
                        <div className="w-48 bg-gray-200 rounded-full h-3">
                          <div
                            className="bg-gradient-to-r from-emerald-500 to-green-500 h-3 rounded-full transition-all duration-500"
                            style={{ width: `${completionRate}%` }}
                          ></div>
                        </div>
                        <div className="text-xs text-gray-500 mt-1">{completionRate}% completion rate</div>
                      </div>
                    </div>
                  );
                })
              ) : analytics.team_performance && analytics.team_performance.length > 0 ? (
                analytics.team_performance.map((member, index) => (
                  <div key={index} className="flex items-center justify-between p-4 bg-white rounded-xl border border-emerald-100 hover:shadow-md transition-all duration-200">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-gradient-to-br from-emerald-400 to-green-500 rounded-full flex items-center justify-center shadow-lg">
                        <span className="text-white font-bold text-lg">
                          {(member.name || 'U').charAt(0).toUpperCase()}
                        </span>
                      </div>
                      <div>
                        <p className="font-bold text-gray-900 text-lg">{member.name}</p>
                        <p className="text-sm text-gray-500">{member.email}</p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="text-sm text-gray-600 mb-2">
                        {member.completed_tasks} of {member.total_assigned} tasks completed
                      </p>
                      <div className="w-48 bg-gray-200 rounded-full h-3">
                        <div
                          className="bg-gradient-to-r from-emerald-500 to-green-500 h-3 rounded-full transition-all duration-500"
                          style={{
                            width: `${member.total_assigned > 0 ? (member.completed_tasks / member.total_assigned) * 100 : 0}%`
                          }}
                        ></div>
                      </div>
                      <div className="text-xs text-gray-500 mt-1">
                        {member.total_assigned > 0 ? Math.round((member.completed_tasks / member.total_assigned) * 100) : 0}% completion rate
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-8 text-gray-500">
                  <Users className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                  <p className="text-sm">No team member data available</p>
                </div>
              )}
            </div>
          </Card>

          {/* Recent Completed Tasks (Project-specific only) */}
          {analytics.recent_completed_tasks && analytics.recent_completed_tasks.length > 0 && (
            <Card className="p-6">
              <div className="flex items-center gap-3 mb-6">
                <div className="p-2 bg-purple-100 rounded-lg">
                  <CheckCircle className="h-5 w-5 text-purple-600" />
                </div>
                <h4 className="text-lg font-semibold text-gray-900">Recent Completed Tasks</h4>
              </div>
              <div className="space-y-3">
                {analytics.recent_completed_tasks.slice(0, 5).map((task, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-green-50 rounded-lg border border-green-200">
                    <div>
                      <p className="font-medium text-gray-900">{task.title}</p>
                      <p className="text-sm text-gray-600">
                        Completed by {task.completed_by}  {new Date(task.completed_at).toLocaleDateString()}
                      </p>
                    </div>
                    <div className="text-right">
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                        task.priority === 'high' ? 'bg-red-100 text-red-800' :
                        task.priority === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {task.priority}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </Card>
          )}

          {/* Project Timeline (Project-specific only) */}
          {projectData && (
            <Card className="p-6">
              <div className="flex items-center gap-3 mb-6">
                <div className="p-2 bg-indigo-100 rounded-lg">
                  <Clock className="h-5 w-5 text-indigo-600" />
                </div>
                <h4 className="text-lg font-semibold text-gray-900">Project Timeline</h4>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="p-4 bg-blue-50 rounded-lg">
                  <p className="text-sm font-medium text-blue-800">Project Start</p>
                  <p className="text-lg font-bold text-blue-900">
                    {new Date(analytics.project_start_date || projectData.created_at).toLocaleDateString()}
                  </p>
                </div>
                <div className="p-4 bg-green-50 rounded-lg">
                  <p className="text-sm font-medium text-green-800">Last Activity</p>
                  <p className="text-lg font-bold text-green-900">
                    {new Date(analytics.last_activity || projectData.updated_at).toLocaleDateString()}
                  </p>
                </div>
              </div>
            </Card>
          )}
        </>
      )}
    </div>
  );
};

export default ProjectAnalytics;
