import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
  Filler,
} from 'chart.js';
import { Bar, Doughnut, Line, Pie } from 'react-chartjs-2';
import Card from '../UI/Card';
import Badge from '../UI/Badge';
import { 
  TrendingUp, 
  Users, 
  Clock, 
  CheckCircle, 
  AlertCircle, 
  XCircle, 
  Target,
  Zap,
  Calendar,
  Award,
  Activity,
  BarChart3,
  PieChart,
  LineChart
} from 'lucide-react';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
  Filler
);

const ProjectAnalytics = ({ analytics, projectName, isLoading = false }) => {
  // Helper function to format duration
  const formatDuration = (days, minutes) => {
    if (!days && !minutes) return 'N/A';
    
    const daysInt = Math.floor(days);
    const hours = Math.floor((days - daysInt) * 24);
    const mins = Math.floor(minutes % 60);
    
    if (daysInt > 0) {
      return `${daysInt}d ${hours}h ${mins}m`;
    } else if (hours > 0) {
      return `${hours}h ${mins}m`;
    } else {
      return `${mins}m`;
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {[1, 2, 3, 4].map((i) => (
            <Card key={i} className="p-6 animate-pulse">
              <div className="h-4 bg-gray-200 rounded mb-2"></div>
              <div className="h-8 bg-gray-200 rounded"></div>
            </Card>
          ))}
        </div>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {[1, 2].map((i) => (
            <Card key={i} className="p-6 animate-pulse">
              <div className="h-6 bg-gray-200 rounded mb-4"></div>
              <div className="h-64 bg-gray-200 rounded"></div>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (!analytics) {
    return (
      <div className="text-center py-12">
        <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
          <TrendingUp className="h-8 w-8 text-gray-400" />
        </div>
        <p className="text-lg text-gray-500">No analytics data available</p>
        <p className="text-sm text-gray-400 mt-2">Analytics will appear here once tasks are created</p>
      </div>
    );
  }


  // Enhanced chart data with gradients and better colors
  const taskStatusData = {
    labels: ['Completed', 'In Progress', 'To Do', 'Overdue'],
    datasets: [
      {
        data: [
          analytics.completed_tasks || 0,
          analytics.in_progress_tasks || 0,
          analytics.todo_tasks || 0,
          analytics.overdue_tasks || 0,
        ],
        backgroundColor: [
          'rgba(16, 185, 129, 0.8)', // Green for completed
          'rgba(59, 130, 246, 0.8)', // Blue for in progress
          'rgba(245, 158, 11, 0.8)', // Yellow for todo
          'rgba(239, 68, 68, 0.8)', // Red for overdue
        ],
        borderColor: [
          'rgba(16, 185, 129, 1)',
          'rgba(59, 130, 246, 1)',
          'rgba(245, 158, 11, 1)',
          'rgba(239, 68, 68, 1)',
        ],
        borderWidth: 3,
        hoverOffset: 8,
        hoverBackgroundColor: [
          'rgba(16, 185, 129, 0.9)',
          'rgba(59, 130, 246, 0.9)',
          'rgba(245, 158, 11, 0.9)',
          'rgba(239, 68, 68, 0.9)',
        ],
      },
    ],
  };

  // Enhanced priority data
  const priorityData = {
    labels: ['High Priority', 'Medium Priority', 'Low Priority'],
    datasets: [
      {
        data: [
          analytics.tasks_by_priority?.find(p => p.priority === 'high')?.count || 0,
          analytics.tasks_by_priority?.find(p => p.priority === 'medium')?.count || 0,
          analytics.tasks_by_priority?.find(p => p.priority === 'low')?.count || 0,
        ],
        backgroundColor: [
          'rgba(239, 68, 68, 0.8)', // Red for high
          'rgba(245, 158, 11, 0.8)', // Yellow for medium
          'rgba(16, 185, 129, 0.8)', // Green for low
        ],
        borderColor: [
          'rgba(239, 68, 68, 1)',
          'rgba(245, 158, 11, 1)',
          'rgba(16, 185, 129, 1)',
        ],
        borderWidth: 3,
        hoverOffset: 8,
      },
    ],
  };

  // Enhanced progress bar data
  const progressData = {
    labels: ['Completed', 'In Progress', 'To Do', 'Overdue'],
    datasets: [
      {
        label: 'Task Distribution',
        data: [
          analytics.completed_tasks || 0,
          analytics.in_progress_tasks || 0,
          analytics.todo_tasks || 0,
          analytics.overdue_tasks || 0,
        ],
        backgroundColor: [
          'rgba(16, 185, 129, 0.7)',
          'rgba(59, 130, 246, 0.7)',
          'rgba(245, 158, 11, 0.7)',
          'rgba(239, 68, 68, 0.7)',
        ],
        borderColor: [
          'rgba(16, 185, 129, 1)',
          'rgba(59, 130, 246, 1)',
          'rgba(245, 158, 11, 1)',
          'rgba(239, 68, 68, 1)',
        ],
        borderWidth: 2,
        borderRadius: 8,
        borderSkipped: false,
      },
    ],
  };

  // New: Team performance data
  const teamPerformanceData = {
    labels: analytics.tasks_by_assignee?.map(assignee => assignee.assignee_name) || ['No Data'],
    datasets: [
      {
        label: 'Tasks Completed',
        data: analytics.tasks_by_assignee?.map(assignee => assignee.completed_count) || [0],
        backgroundColor: 'rgba(59, 130, 246, 0.8)',
        borderColor: 'rgba(59, 130, 246, 1)',
        borderWidth: 2,
        borderRadius: 6,
        borderSkipped: false,
      },
      {
        label: 'Tasks In Progress',
        data: analytics.tasks_by_assignee?.map(assignee => assignee.in_progress_count) || [0],
        backgroundColor: 'rgba(245, 158, 11, 0.8)',
        borderColor: 'rgba(245, 158, 11, 1)',
        borderWidth: 2,
        borderRadius: 6,
        borderSkipped: false,
      },
    ],
  };

  // New: Timeline data for project progress
  const timelineData = {
    labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4', 'Week 5', 'Week 6'],
    datasets: [
      {
        label: 'Tasks Completed',
        data: [2, 5, 8, 12, 15, 18],
        borderColor: 'rgba(16, 185, 129, 1)',
        backgroundColor: 'rgba(16, 185, 129, 0.1)',
        borderWidth: 3,
        fill: true,
        tension: 0.4,
        pointBackgroundColor: 'rgba(16, 185, 129, 1)',
        pointBorderColor: '#fff',
        pointBorderWidth: 2,
        pointRadius: 6,
        pointHoverRadius: 8,
      },
      {
        label: 'Tasks Created',
        data: [5, 8, 12, 15, 18, 20],
        borderColor: 'rgba(59, 130, 246, 1)',
        backgroundColor: 'rgba(59, 130, 246, 0.1)',
        borderWidth: 3,
        fill: true,
        tension: 0.4,
        pointBackgroundColor: 'rgba(59, 130, 246, 1)',
        pointBorderColor: '#fff',
        pointBorderWidth: 2,
        pointRadius: 6,
        pointHoverRadius: 8,
      },
    ],
  };

  // Enhanced chart options with modern styling
  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
        labels: {
          padding: 20,
          usePointStyle: true,
          font: {
            size: 12,
            weight: '500',
          },
          color: '#374151',
        },
      },
      tooltip: {
        backgroundColor: 'rgba(17, 24, 39, 0.95)',
        titleColor: 'white',
        bodyColor: 'white',
        borderColor: 'rgba(59, 130, 246, 0.3)',
        borderWidth: 1,
        cornerRadius: 8,
        displayColors: true,
        titleFont: {
          size: 14,
          weight: 'bold',
        },
        bodyFont: {
          size: 13,
        },
        padding: 12,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        grid: {
          color: 'rgba(156, 163, 175, 0.1)',
          drawBorder: false,
        },
        ticks: {
          color: '#6b7280',
          font: {
            size: 11,
          },
        },
      },
      x: {
        grid: {
          display: false,
        },
        ticks: {
          color: '#6b7280',
          font: {
            size: 11,
          },
        },
      },
    },
  };

  const doughnutOptions = {
    ...chartOptions,
    cutout: '65%',
    plugins: {
      ...chartOptions.plugins,
      legend: {
        position: 'right',
        labels: {
          padding: 20,
          usePointStyle: true,
          font: {
            size: 12,
            weight: '500',
          },
          color: '#374151',
        },
      },
    },
  };

  const lineOptions = {
    ...chartOptions,
    plugins: {
      ...chartOptions.plugins,
      legend: {
        position: 'top',
        labels: {
          padding: 20,
          usePointStyle: true,
          font: {
            size: 12,
            weight: '500',
          },
          color: '#374151',
        },
      },
    },
    scales: {
      ...chartOptions.scales,
      y: {
        ...chartOptions.scales.y,
        grid: {
          color: 'rgba(156, 163, 175, 0.1)',
          drawBorder: false,
        },
      },
      x: {
        ...chartOptions.scales.x,
        grid: {
          color: 'rgba(156, 163, 175, 0.1)',
          drawBorder: false,
        },
      },
    },
  };

  // Check if this is project-specific analytics or general analytics
  const isProjectSpecific = analytics?.project || projectName;
  const projectData = analytics?.project;

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="text-center mb-8">
        <h3 className="text-2xl font-bold text-gray-900 mb-2">
          {isProjectSpecific ? `${projectName || projectData?.name} Analytics` : 'Overall Analytics'}
        </h3>
        <p className="text-gray-600">
          {isProjectSpecific 
            ? 'Project-specific insights and performance metrics' 
            : 'Comprehensive insights across all projects'
          }
        </p>
        {isProjectSpecific && projectData && (
          <div className="mt-4 p-4 bg-blue-50 rounded-lg border border-blue-200">
            <p className="text-sm text-blue-800">
              <strong>Project:</strong> {projectData.name}
              {projectData.description && (
                <span className="block mt-1 text-blue-600">{projectData.description}</span>
              )}
            </p>
          </div>
        )}
      </div>

      {/* Enhanced Stats Grid with Modern Design */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="p-6 bg-gradient-to-br from-blue-50 to-blue-100 border-blue-200 hover:shadow-lg transition-all duration-300">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <div className="p-3 rounded-xl bg-gradient-to-br from-blue-500 to-blue-600 shadow-lg">
                <Target className="h-6 w-6 text-white" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-semibold text-blue-700">Total Tasks</p>
                <p className="text-3xl font-bold text-blue-900">{analytics.total_tasks || 0}</p>
              </div>
            </div>
            <div className="text-right">
              <div className="w-12 h-12 bg-blue-200 rounded-full flex items-center justify-center">
                <span className="text-lg font-bold text-blue-700">100%</span>
              </div>
            </div>
          </div>
        </Card>

        <Card className="p-6 bg-gradient-to-br from-green-50 to-green-100 border-green-200 hover:shadow-lg transition-all duration-300">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <div className="p-3 rounded-xl bg-gradient-to-br from-green-500 to-green-600 shadow-lg">
                <CheckCircle className="h-6 w-6 text-white" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-semibold text-green-700">Completed</p>
                <p className="text-3xl font-bold text-green-900">{analytics.completed_tasks || 0}</p>
              </div>
            </div>
            <div className="text-right">
              <div className="w-12 h-12 bg-green-200 rounded-full flex items-center justify-center">
                <span className="text-lg font-bold text-green-700">
                  {analytics.total_tasks ? Math.round((analytics.completed_tasks / analytics.total_tasks) * 100) : 0}%
                </span>
              </div>
            </div>
          </div>
        </Card>

        <Card className="p-6 bg-gradient-to-br from-yellow-50 to-yellow-100 border-yellow-200 hover:shadow-lg transition-all duration-300">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <div className="p-3 rounded-xl bg-gradient-to-br from-yellow-500 to-yellow-600 shadow-lg">
                <Activity className="h-6 w-6 text-white" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-semibold text-yellow-700">In Progress</p>
                <p className="text-3xl font-bold text-yellow-900">{analytics.in_progress_tasks || 0}</p>
              </div>
            </div>
            <div className="text-right">
              <div className="w-12 h-12 bg-yellow-200 rounded-full flex items-center justify-center">
                <span className="text-lg font-bold text-yellow-700">
                  {analytics.total_tasks ? Math.round((analytics.in_progress_tasks / analytics.total_tasks) * 100) : 0}%
                </span>
              </div>
            </div>
          </div>
        </Card>

        <Card className="p-6 bg-gradient-to-br from-red-50 to-red-100 border-red-200 hover:shadow-lg transition-all duration-300">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <div className="p-3 rounded-xl bg-gradient-to-br from-red-500 to-red-600 shadow-lg">
                <AlertCircle className="h-6 w-6 text-white" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-semibold text-red-700">Overdue</p>
                <p className="text-3xl font-bold text-red-900">{analytics.overdue_tasks || 0}</p>
              </div>
            </div>
            <div className="text-right">
              <div className="w-12 h-12 bg-red-200 rounded-full flex items-center justify-center">
                <span className="text-lg font-bold text-red-700">
                  {analytics.total_tasks ? Math.round((analytics.overdue_tasks / analytics.total_tasks) * 100) : 0}%
                </span>
              </div>
            </div>
          </div>
        </Card>
      </div>

      {/* Completion Rate */}
      <Card className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">Completion Rate</h3>
          <Badge variant="success" size="lg">
            {analytics.completion_rate !== undefined 
              ? `${analytics.completion_rate}%`
              : (analytics.total_tasks > 0 
                  ? Math.round((analytics.completed_tasks / analytics.total_tasks) * 100)
                  : 0) + '%'}
          </Badge>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div
            className="bg-green-600 h-2 rounded-full transition-all duration-300"
            style={{ 
              width: `${analytics.completion_rate !== undefined 
                ? analytics.completion_rate 
                : (analytics.total_tasks > 0 
                    ? Math.round((analytics.completed_tasks / analytics.total_tasks) * 100)
                    : 0)}%` 
            }}
          ></div>
        </div>
      </Card>


      {/* Enhanced Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Task Status Distribution - Enhanced */}
        <Card className="p-6 bg-gradient-to-br from-blue-50 to-indigo-50 border-blue-200">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="p-3 bg-gradient-to-br from-blue-500 to-blue-600 rounded-xl shadow-lg">
                <PieChart className="h-6 w-6 text-white" />
              </div>
              <div>
                <h4 className="text-lg font-bold text-gray-900">Task Status Distribution</h4>
                <p className="text-sm text-gray-600">Visual breakdown of task progress</p>
              </div>
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold text-blue-600">{analytics.total_tasks || 0}</div>
              <div className="text-sm text-gray-500">Total Tasks</div>
            </div>
          </div>
          <div className="h-80">
            <Doughnut data={taskStatusData} options={doughnutOptions} />
          </div>
        </Card>

        {/* Priority Distribution - Enhanced */}
        <Card className="p-6 bg-gradient-to-br from-orange-50 to-red-50 border-orange-200">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="p-3 bg-gradient-to-br from-orange-500 to-red-500 rounded-xl shadow-lg">
                <AlertCircle className="h-6 w-6 text-white" />
              </div>
              <div>
                <h4 className="text-lg font-bold text-gray-900">Priority Distribution</h4>
                <p className="text-sm text-gray-600">Task priority breakdown</p>
              </div>
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold text-orange-600">
                {analytics.tasks_by_priority?.find(p => p.priority === 'high')?.count || 0}
              </div>
              <div className="text-sm text-gray-500">High Priority</div>
            </div>
          </div>
          <div className="h-80">
            <Pie data={priorityData} options={doughnutOptions} />
          </div>
        </Card>

        {/* Team Performance - New Chart */}
        <Card className="p-6 bg-gradient-to-br from-green-50 to-emerald-50 border-green-200">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="p-3 bg-gradient-to-br from-green-500 to-emerald-500 rounded-xl shadow-lg">
                <BarChart3 className="h-6 w-6 text-white" />
              </div>
              <div>
                <h4 className="text-lg font-bold text-gray-900">Team Performance</h4>
                <p className="text-sm text-gray-600">Tasks by team member</p>
              </div>
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold text-green-600">
                {analytics.tasks_by_assignee?.length || 0}
              </div>
              <div className="text-sm text-gray-500">Team Members</div>
            </div>
          </div>
          <div className="h-80">
            <Bar data={teamPerformanceData} options={chartOptions} />
          </div>
        </Card>

        {/* Project Timeline - New Chart */}
        <Card className="p-6 bg-gradient-to-br from-purple-50 to-pink-50 border-purple-200">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="p-3 bg-gradient-to-br from-purple-500 to-pink-500 rounded-xl shadow-lg">
                <LineChart className="h-6 w-6 text-white" />
              </div>
              <div>
                <h4 className="text-lg font-bold text-gray-900">Project Timeline</h4>
                <p className="text-sm text-gray-600">Progress over time</p>
              </div>
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold text-purple-600">6</div>
              <div className="text-sm text-gray-500">Weeks Tracked</div>
            </div>
          </div>
          <div className="h-80">
            <Line data={timelineData} options={lineOptions} />
          </div>
        </Card>

        {/* Task Status Overview - Enhanced */}
        <Card className="p-6 lg:col-span-2 bg-gradient-to-br from-gray-50 to-slate-50 border-gray-200">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="p-3 bg-gradient-to-br from-gray-500 to-slate-500 rounded-xl shadow-lg">
                <TrendingUp className="h-6 w-6 text-white" />
              </div>
              <div>
                <h4 className="text-lg font-bold text-gray-900">Task Status Overview</h4>
                <p className="text-sm text-gray-600">Comprehensive task distribution analysis</p>
              </div>
            </div>
            <div className="flex gap-4 text-center">
              <div>
                <div className="text-xl font-bold text-green-600">{analytics.completed_tasks || 0}</div>
                <div className="text-xs text-gray-500">Completed</div>
              </div>
              <div>
                <div className="text-xl font-bold text-yellow-600">{analytics.in_progress_tasks || 0}</div>
                <div className="text-xs text-gray-500">In Progress</div>
              </div>
              <div>
                <div className="text-xl font-bold text-red-600">{analytics.overdue_tasks || 0}</div>
                <div className="text-xs text-gray-500">Overdue</div>
              </div>
            </div>
          </div>
          <div className="h-80">
            <Bar data={progressData} options={chartOptions} />
          </div>
        </Card>
      </div>

      {/* Additional Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <Card className="p-6 text-center">
          <div className="p-3 bg-yellow-100 rounded-full w-12 h-12 flex items-center justify-center mx-auto mb-3">
            <Clock className="h-6 w-6 text-yellow-600" />
          </div>
          <h5 className="font-semibold text-gray-900 mb-1">Avg. Task Duration</h5>
          <p className="text-2xl font-bold text-yellow-600">
            {formatDuration(analytics.avg_task_duration, analytics.avg_task_duration_minutes)}
          </p>
          {analytics.avg_task_duration_minutes && (
            <p className="text-xs text-gray-500 mt-1">
              {Math.round(analytics.avg_task_duration_minutes)} minutes total
            </p>
          )}
        </Card>

        <Card className="p-6 text-center">
          <div className="p-3 bg-red-100 rounded-full w-12 h-12 flex items-center justify-center mx-auto mb-3">
            <XCircle className="h-6 w-6 text-red-600" />
          </div>
          <h5 className="font-semibold text-gray-900 mb-1">Overdue Tasks</h5>
          <p className="text-2xl font-bold text-red-600">{analytics.overdue_tasks || 0}</p>
        </Card>
      </div>

      {/* Project-specific sections */}
      {isProjectSpecific && (
        <>
          {/* Team Performance (Project-specific only) */}
          <Card className="p-6 bg-gradient-to-br from-emerald-50 to-green-50 border-emerald-200">
            <div className="flex items-center justify-between mb-6">
              <div className="flex items-center gap-3">
                <div className="p-3 bg-gradient-to-br from-emerald-500 to-green-500 rounded-xl shadow-lg">
                  <Users className="h-6 w-6 text-white" />
                </div>
                <div>
                  <h4 className="text-lg font-bold text-gray-900">Team Performance</h4>
                  <p className="text-sm text-gray-600">Team member productivity and task completion</p>
                </div>
              </div>
              <div className="text-right">
                <div className="text-2xl font-bold text-emerald-600">
                  {analytics.tasks_by_assignee?.length || analytics.team_performance?.length || 0}
                </div>
                <div className="text-sm text-gray-500">Team Members</div>
              </div>
            </div>
            
            <div className="space-y-4">
              {analytics.tasks_by_assignee && analytics.tasks_by_assignee.length > 0 ? (
                analytics.tasks_by_assignee.map((member, index) => {
                  const totalTasks = (member.completed_count || 0) + (member.in_progress_count || 0) + (member.todo_count || 0);
                  const completionRate = totalTasks > 0 ? Math.round(((member.completed_count || 0) / totalTasks) * 100) : 0;
                  
                  return (
                    <div key={index} className="flex items-center justify-between p-4 bg-white rounded-xl border border-emerald-100 hover:shadow-md transition-all duration-200">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gradient-to-br from-emerald-400 to-green-500 rounded-full flex items-center justify-center shadow-lg">
                          <span className="text-white font-bold text-lg">
                            {(member.assignee_name || 'U').charAt(0).toUpperCase()}
                          </span>
                        </div>
                        <div>
                          <p className="font-bold text-gray-900 text-lg">{member.assignee_name || 'Unknown User'}</p>
                          <p className="text-sm text-gray-500">Team Member</p>
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="flex items-center gap-4 mb-2">
                          <div className="text-center">
                            <div className="text-xl font-bold text-green-600">{member.completed_count || 0}</div>
                            <div className="text-xs text-gray-500">Completed</div>
                          </div>
                          <div className="text-center">
                            <div className="text-xl font-bold text-yellow-600">{member.in_progress_count || 0}</div>
                            <div className="text-xs text-gray-500">In Progress</div>
                          </div>
                          <div className="text-center">
                            <div className="text-xl font-bold text-gray-600">{member.todo_count || 0}</div>
                            <div className="text-xs text-gray-500">To Do</div>
                          </div>
                        </div>
                        <div className="w-48 bg-gray-200 rounded-full h-3">
                          <div
                            className="bg-gradient-to-r from-emerald-500 to-green-500 h-3 rounded-full transition-all duration-500"
                            style={{ width: `${completionRate}%` }}
                          ></div>
                        </div>
                        <div className="text-xs text-gray-500 mt-1">{completionRate}% completion rate</div>
                      </div>
                    </div>
                  );
                })
              ) : analytics.team_performance && analytics.team_performance.length > 0 ? (
                analytics.team_performance.map((member, index) => (
                  <div key={index} className="flex items-center justify-between p-4 bg-white rounded-xl border border-emerald-100 hover:shadow-md transition-all duration-200">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-gradient-to-br from-emerald-400 to-green-500 rounded-full flex items-center justify-center shadow-lg">
                        <span className="text-white font-bold text-lg">
                          {(member.name || 'U').charAt(0).toUpperCase()}
                        </span>
                      </div>
                      <div>
                        <p className="font-bold text-gray-900 text-lg">{member.name}</p>
                        <p className="text-sm text-gray-500">{member.email}</p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="text-sm text-gray-600 mb-2">
                        {member.completed_tasks} of {member.total_assigned} tasks completed
                      </p>
                      <div className="w-48 bg-gray-200 rounded-full h-3">
                        <div
                          className="bg-gradient-to-r from-emerald-500 to-green-500 h-3 rounded-full transition-all duration-500"
                          style={{
                            width: `${member.total_assigned > 0 ? (member.completed_tasks / member.total_assigned) * 100 : 0}%`
                          }}
                        ></div>
                      </div>
                      <div className="text-xs text-gray-500 mt-1">
                        {member.total_assigned > 0 ? Math.round((member.completed_tasks / member.total_assigned) * 100) : 0}% completion rate
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-8 text-gray-500">
                  <Users className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                  <p className="text-sm">No team member data available</p>
                </div>
              )}
            </div>
          </Card>

          {/* Recent Completed Tasks (Project-specific only) */}
          {analytics.recent_completed_tasks && analytics.recent_completed_tasks.length > 0 && (
            <Card className="p-6">
              <div className="flex items-center gap-3 mb-6">
                <div className="p-2 bg-purple-100 rounded-lg">
                  <CheckCircle className="h-5 w-5 text-purple-600" />
                </div>
                <h4 className="text-lg font-semibold text-gray-900">Recent Completed Tasks</h4>
              </div>
              <div className="space-y-3">
                {analytics.recent_completed_tasks.slice(0, 5).map((task, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-green-50 rounded-lg border border-green-200">
                    <div>
                      <p className="font-medium text-gray-900">{task.title}</p>
                      <p className="text-sm text-gray-600">
                        Completed by {task.completed_by} • {new Date(task.completed_at).toLocaleDateString()}
                      </p>
                    </div>
                    <div className="text-right">
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                        task.priority === 'high' ? 'bg-red-100 text-red-800' :
                        task.priority === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {task.priority}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </Card>
          )}

          {/* Project Timeline (Project-specific only) */}
          {projectData && (
            <Card className="p-6">
              <div className="flex items-center gap-3 mb-6">
                <div className="p-2 bg-indigo-100 rounded-lg">
                  <Clock className="h-5 w-5 text-indigo-600" />
                </div>
                <h4 className="text-lg font-semibold text-gray-900">Project Timeline</h4>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="p-4 bg-blue-50 rounded-lg">
                  <p className="text-sm font-medium text-blue-800">Project Start</p>
                  <p className="text-lg font-bold text-blue-900">
                    {new Date(analytics.project_start_date || projectData.created_at).toLocaleDateString()}
                  </p>
                </div>
                <div className="p-4 bg-green-50 rounded-lg">
                  <p className="text-sm font-medium text-green-800">Last Activity</p>
                  <p className="text-lg font-bold text-green-900">
                    {new Date(analytics.last_activity || projectData.updated_at).toLocaleDateString()}
                  </p>
                </div>
              </div>
            </Card>
          )}
        </>
      )}
    </div>
  );
};

export default ProjectAnalytics;
