import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { useAuth } from '../../contexts/AuthContext';
import { tasksAPI } from '../../services/api';
import Card from '../../components/UI/Card';
import Badge from '../../components/UI/Badge';
import LoadingSpinner from '../../components/UI/LoadingSpinner';
import TaskModal from '../../components/Tasks/TaskModal';
import {
  Plus,
  Calendar,
  User,
  AlertCircle,
  CheckCircle,
  Clock,
} from 'lucide-react';
import { format } from 'date-fns';
import toast from 'react-hot-toast';

const Kanban = () => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [showModal, setShowModal] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [draggedTask, setDraggedTask] = useState(null);
  const [dragOverColumn, setDragOverColumn] = useState(null);

  // Fetch kanban tasks
  const { data: kanbanData, isLoading, error } = useQuery(
    'kanbanTasks',
    tasksAPI.getKanbanTasks,
    {
      // Refresh frequently so Scrum Master sees employees' updates quickly
      refetchInterval: 5000,
    }
  );

  // Update task status mutation
  const updateStatusMutation = useMutation(
    ({ taskId, status }) => tasksAPI.updateTaskStatus(taskId, status),
    {
      // Optimistic update for instant UI feedback
      onMutate: async ({ taskId, status }) => {
        await queryClient.cancelQueries('kanbanTasks');
        const previous = queryClient.getQueryData('kanbanTasks');
        if (previous) {
          // Handle both direct data and axios response format
          const data = previous.data || previous;
          const clone = {
            data: {
              todo: [...(data.todo || [])],
              in_progress: [...(data.in_progress || [])],
              done: [...(data.done || [])],
            }
          };
          // Remove task from any column
          let task;
          for (const col of ['todo', 'in_progress', 'done']) {
            const idx = clone.data[col].findIndex((t) => t.id === taskId);
            if (idx !== -1) {
              task = { ...clone.data[col][idx] };
              clone.data[col].splice(idx, 1);
              break;
            }
          }
          // Insert into new column
          if (task) {
            task.status = status;
            clone.data[status].unshift(task);
            queryClient.setQueryData('kanbanTasks', clone);
          }
        }
        return { previous };
      },
      onError: (error, _vars, context) => {
        if (context?.previous) {
          queryClient.setQueryData('kanbanTasks', context.previous);
        }
        toast.error(error.response?.data?.detail || 'Failed to update task status');
      },
      onSettled: () => {
        queryClient.invalidateQueries('kanbanTasks');
      },
    }
  );

  const handleDragStart = (e, task) => {
    setDraggedTask(task);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragEnd = () => {
    setDraggedTask(null);
    setDragOverColumn(null);
  };

  const handleDragOver = (e, columnId) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
    setDragOverColumn(columnId);
  };

  const handleDrop = (e, newStatus) => {
    e.preventDefault();
    setDragOverColumn(null);
    
    if (!draggedTask || draggedTask.status === newStatus) {
      setDraggedTask(null);
      return;
    }

    // Check permissions
    if (user?.role === 'employee' && draggedTask.assignee !== user.id) {
      toast.error('You can only update your assigned tasks');
      setDraggedTask(null);
      return;
    }

    // Show immediate feedback
    toast.success(`Moving task to ${newStatus.replace('_', ' ')}...`);

    updateStatusMutation.mutate({
      taskId: draggedTask.id,
      status: newStatus,
    });

    setDraggedTask(null);
  };

  const handleEditTask = (task) => {
    setEditingTask(task);
    setShowModal(true);
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'done':
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case 'in_progress':
        return <Clock className="h-4 w-4 text-yellow-600" />;
      default:
        return <AlertCircle className="h-4 w-4 text-gray-600" />;
    }
  };

  const getPriorityBadge = (priority) => {
    const variants = {
      low: 'gray',
      medium: 'primary',
      high: 'warning',
      critical: 'danger',
    };
    return (
      <Badge variant={variants[priority]} size="sm">
        {priority.toUpperCase()}
      </Badge>
    );
  };

  // Normalize axios response: extract .data which contains { todo, in_progress, done }
  const data = kanbanData?.data || {};

  const columns = [
    {
      id: 'todo',
      title: 'To Do',
      color: 'bg-gray-100',
      tasks: data?.todo || [],
    },
    {
      id: 'in_progress',
      title: 'In Progress',
      color: 'bg-yellow-100',
      tasks: data?.in_progress || [],
    },
    {
      id: 'done',
      title: 'Done',
      color: 'bg-green-100',
      tasks: data?.done || [],
    },
  ];

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Failed to load kanban board</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Kanban Board</h1>
          <p className="text-gray-600">Drag and drop tasks to update their status</p>
        </div>
        {/* Task creation is handled in the separate Tasks page */}
        <a
          href="/tasks"
          className="btn btn-primary flex items-center gap-2"
        >
          <Plus className="h-4 w-4" />
          Go to Tasks
        </a>
      </div>

      {/* Kanban Board */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {columns.map((column) => (
          <div key={column.id} className="space-y-4">
            {/* Column Header */}
            <div className={`p-4 rounded-lg ${column.color}`}>
              <div className="flex items-center justify-between">
                <h3 className="font-semibold text-gray-900">{column.title}</h3>
                <span className="bg-white bg-opacity-50 px-2 py-1 rounded-full text-sm font-medium">
                  {column.tasks.length}
                </span>
              </div>
            </div>

            {/* Tasks */}
            <div
              className={`min-h-96 space-y-3 transition-colors duration-200 ${
                dragOverColumn === column.id && draggedTask && draggedTask.status !== column.id
                  ? 'bg-blue-50 border-2 border-blue-300 border-dashed rounded-lg p-2'
                  : ''
              }`}
              onDragOver={(e) => handleDragOver(e, column.id)}
              onDragLeave={() => setDragOverColumn(null)}
              onDrop={(e) => handleDrop(e, column.id)}
            >
              {column.tasks.map((task) => (
                <Card
                  key={task.id}
                  className={`p-4 cursor-move hover:shadow-md transition-all duration-200 ${
                    draggedTask?.id === task.id ? 'opacity-50 scale-95' : ''
                  }`}
                  draggable
                  onDragStart={(e) => handleDragStart(e, task)}
                  onDragEnd={handleDragEnd}
                  onClick={() => {
                    if (user?.role === 'scrum_master') {
                      handleEditTask(task);
                    }
                  }}
                >
                  <div className="space-y-3">
                    {/* Task Header */}
                    <div className="flex items-start justify-between">
                      <div className="flex items-center gap-2">
                        {getStatusIcon(task.status)}
                        <h4 className="font-medium text-gray-900 line-clamp-2">
                          {task.title}
                        </h4>
                      </div>
                      {getPriorityBadge(task.priority)}
                    </div>

                    {/* Task Description */}
                    {task.description && (
                      <p className="text-sm text-gray-600 line-clamp-2">
                        {task.description}
                      </p>
                    )}

                    {/* Task Meta */}
                    <div className="space-y-2">
                      <div className="flex items-center gap-1 text-xs text-gray-500">
                        <User className="h-3 w-3" />
                        {task.assignee_name || 'Unassigned'}
                      </div>
                      
                      {task.due_date && (
                        <div className="flex items-center gap-1 text-xs text-gray-500">
                          <Calendar className="h-3 w-3" />
                          {format(new Date(task.due_date), 'MMM dd, yyyy')}
                          {task.is_overdue && (
                            <Badge variant="danger" size="sm">Overdue</Badge>
                          )}
                        </div>
                      )}

                      <div className="text-xs text-gray-500">
                        Project: {task.project_name}
                      </div>
                    </div>

                    {/* Comments Count */}
                    {task.comments_count > 0 && (
                      <div className="text-xs text-blue-600">
                        {task.comments_count} comment{task.comments_count !== 1 ? 's' : ''}
                      </div>
                    )}
                  </div>
                </Card>
              ))}

              {/* Empty State */}
              {column.tasks.length === 0 && (
                <div className="flex items-center justify-center h-32 border-2 border-dashed border-gray-300 rounded-lg">
                  <p className="text-gray-500 text-sm">No tasks</p>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Task Modal */}
      {showModal && (
        <TaskModal
          task={editingTask}
          onClose={() => {
            setShowModal(false);
            setEditingTask(null);
          }}
          onSuccess={() => {
            setShowModal(false);
            setEditingTask(null);
            queryClient.invalidateQueries('kanbanTasks');
          }}
        />
      )}
    </div>
  );
};

export default Kanban;
